$$
F_{p q}=\frac{1}{\sqrt{M N}}\sum_{m=0}^{M-1}\sum_{n=0}^{N-1}X_{m n}e^{-i2\pi(\frac{m p}{M}+\frac{n q}{N})};\;X_{m n}=\frac{1}{\sqrt{M N}}\sum_{p=0}^{M-1}\sum_{q=0}^{N-1}F_{p q}e^{i2\pi(\frac{m p}{M}+\frac{n q}{N})}
$$

$$
\frac{\partial F_{p q}}{\partial X_{k l}}=\frac{1}{\sqrt{M N}}e^{-i2\pi(\frac{k p}{M}+\frac{l q}{N})}
$$

$$
\begin{array}{r l}{\frac{\partial}{\partial\hat{X}_{k l}}|F_{p q}-F_{p q}^{*}|^{2}=\frac{\partial}{\partial\hat{X}_{k l}}[|F_{p q}|^{2}-F_{p q}^{*}\hat{F}_{p q}-F_{p q}\hat{F}_{p q}^{*}+\hat{F}_{p q}\hat{F}_{p q}^{*}]}\\ {=\frac{1}{\sqrt{M N}}[-F_{p q}^{*}e^{-i2\pi(\frac{k p}{M}+\frac{l q}{N})}-F_{p q}e^{i2\pi(\frac{k p}{M}+\frac{l q}{N})}+\hat{F}_{p q}^{*}e^{-i2\pi(\frac{k p}{M}+\frac{l q}{N})}+\hat{F}_{p q}e^{i2\pi(\frac{k p}{M}+\frac{l q}{N})}].}\end{array}
$$

$$
\begin{array}{r l}{\frac{\partial L_{2}(F,\bar{F})}{\partial\bar{X}_{k l}}=\frac{1}{M N}\displaystyle\sum_{p=0}^{M-1/N-1}\frac{\partial}{\partial\bar{X}_{k l}}|F_{p q}-F_{p q}^{*}|^{2}}\\ &{=-\frac{1}{(\sqrt{M N})^{3}}\displaystyle\sum_{p=0}^{M-1}\left[F_{p q}^{*}e^{-i2\pi(\frac{k p}{M}+\frac{l q}{N})}+F_{p q}e^{i2\pi(\frac{k p}{M}+\frac{l q}{N})}\right.}\\ &{\quad\left.-\bar{F}_{p q}^{*}e^{-i2\pi(\frac{k p}{M}+\frac{l q}{N})}-\bar{F}_{p q}e^{i2\pi(\frac{k p}{M}+\frac{l q}{N})}\right]}\\ &{=-\frac{1}{M N}[X_{k}^{*}+X_{k l}-\bar{X}_{k l}^{*}-\bar{X}_{k l}]}\\ &{=-\frac{2}{M N}[X_{k l}-\bar{X}_{k}]}\\ {\frac{\partial L_{2}(F,\bar{F})}{\partial\bar{X}_{k l}}=\frac{\partial L_{2}(X,\bar{X})}{\partial\bar{X}_{k l}}}\end{array}
$$

$$
\begin{array}{c}{{\displaystyle|F_{p q}|^{2}=F_{p q}F_{p q}^{*}}}\\ {{\displaystyle2\,|F_{p q}|\,\frac{\partial|F_{p q}|}{\partial X_{k l}}=F_{p q}\frac{\partial F_{p q}^{*}}{\partial X_{k l}}+\frac{\partial F_{p q}}{\partial X_{k l}}F_{p q}^{*}}}\\ {{\displaystyle\frac{\partial|F_{p q}|}{\partial X_{k l}}=\frac{1}{2\sqrt{M N}}[e^{i(\theta_{p q}+\alpha_{p q,k l})}+e^{-i(\theta_{p q}+\alpha_{p q,k l})}]}}\\ {{\displaystyle=\frac{1}{\sqrt{M N}}\cos(\theta_{p q}+\alpha_{p q,k l}),}}\end{array}
$$

$$
\begin{array}{r l}{\mathrm{FaL}(X,\hat{X})=\frac{1}{M N}\frac{M-1-N-1}{\gamma_{0}}(|F|_{n}-|\hat{F}|_{n})^{2}}\\ &{=\frac{1}{M N}\frac{M-1-N-1}{\gamma_{0}}(|F|_{n}^{2}+|F|_{n}^{2}-2|F|_{n}|\hat{F}|_{n})}\\ &{=\frac{1}{M N}\frac{M-1-N-1}{\gamma_{0}}(x_{n}^{2}+\hat{X}_{p}^{2})-\frac{1}{M N}\frac{M-1-N-1}{\gamma_{0}}\sum_{j=0}^{N-1}|F|_{n}|\hat{F}|_{n}}\\ &{=\frac{1}{M N}\frac{M-1-N-1}{\gamma_{0}}(x_{n}^{2}+\hat{X}_{p}^{2}-2X_{p}\hat{X}_{p})+}\\ &{\overset{}{\underset{\mathrm{\tiny~1/~1}}{=}}\frac{1^{N-1-N-1}}{\gamma_{0}}\frac{1}{\gamma_{0}}(X_{p}^{2}+\hat{X}_{p}^{2}-2X_{p}\hat{X}_{p})+}\\ &{\overset{}{\underset{\mathrm{\tiny~1/~1}}{=}}\frac{1^{N-1-N-1}}{\gamma_{0}}\frac{1}{\gamma_{0}n}\hat{\X}_{p_{n}}\frac{1}{M N}\frac{M-1-N-1}{\underset{\mathrm{\tiny~2/~1}}{=}}2|F|_{n}|\hat{F}|_{n}}\\ &{=L(X,\hat{X})+\frac{1}{M N}\frac{M-1-N-1}{\underset{\mathrm{\tiny~2/~1}}{=}}2X_{p_{n}}\hat{\ X}_{p_{n}}-\frac{1}{M N}\frac{M-1-N-1}{\underset{\mathrm{\tiny~2/~1}}{=}}2|F|_{n}|\hat{F}|_{n}.}\end{array}
$$
