\begin{array}{c}{{\{\triangle W_{i}^{t}\}_{\mathrm{init}}=\triangle\bar{W}^{t-1}}}\\ {{\{\{B_{i,l}^{t}A_{i,l}^{t}\}_{l=1}^{L}\}_{\mathrm{init}}=\{\bar{B}_{l}^{t-1}\bar{A}_{l}^{t-1}\}_{l=1}^{L}}}\end{array}
$$

$$
\begin{array}{r l}&{\triangle\bar{W}_{l}^{t}=\mathbf{Agg}_{i\in\Omega_{N}}(\triangle W_{i,l}^{t})}\\ &{\qquad=(\frac{1}{N}\sum_{i\in\Omega_{N}}B_{i,l}^{t})(\frac{1}{N}\sum_{i\in\Omega_{N}}A_{i,l}^{t})}\\ &{\qquad=\bar{B}_{l}^{t}\bar{A}_{l}^{t}}\end{array}
$$

$$
\triangle\widetilde{W}^{t-1}=\{\widetilde{B}_{l}^{t-1}\widetilde{A}_{l}^{t-1}\}_{\in\Psi^{t-1}}
$$

$$
\begin{array}{r l}&{\{\{\bar{B}_{m,l}^{t-1}\bar{A}_{m,l}^{t-1}\}_{\in\Psi_{t-1}}\}_{\mathrm{init}}=\triangle\widetilde{W}^{t-1}}\\ &{\triangle\bar{W}_{m}^{t-1}=\{\bar{B}_{m,l}^{t-1}\bar{A}_{m,l}^{t-1}\}_{l=1}^{L}}\end{array}
$$

$$
\begin{array}{c}{\{\triangle W_{m,i}^{t}\}_{\mathrm{init}}=\triangle\bar{W}_{m}^{t-1}}\\ {\triangle W_{m,i}^{t}=\{\triangle W_{m,i,l}^{t}\}_{l=1}^{L}=\{B_{m,i,l}^{t}A_{m,i,l}^{t}\}_{l=1}^{L}}\end{array}
$$

$$
\begin{array}{r l}&{\triangle\bar{W}_{m,l}^{t}=\mathbf{A}\mathbf{g}\mathbf{g}_{i\in\Omega_{N}}(\triangle W_{m,i,l}^{t})}\\ &{\qquad\quad=(\frac{1}{N}\displaystyle\sum_{i\in\Omega_{N}}B_{m,i,l}^{t})(\frac{1}{N}\displaystyle\sum_{i\in\Omega_{N}}A_{m,i,l}^{t})}\\ &{\qquad\quad=\bar{B}_{m,l}^{t}\bar{A}_{m,l}^{t}}\end{array}
$$

$$
\begin{array}{r l}&{\triangle\widetilde{W}_{l}^{t}=\mathsf{A g g}_{m\in M}(\triangle\bar{W}_{m,l}^{t})}\\ &{\qquad=(\frac{1}{M}\displaystyle\sum_{m\in M}\bar{B}_{m,l}^{t})(\frac{1}{M}\displaystyle\sum_{m\in M}\bar{A}_{m,l}^{t})}\\ &{\qquad=\widetilde{B}_{l}^{t}\tilde{A}_{l}^{t}}\end{array}
$$

$$
\{\bar{B}_{m,l}^{t-1}\bar{A}_{m,l}^{t-1}\}_{\mathrm{init}}=\left\{(\bar{B}_{m,l}^{t-1}+\alpha\frac{\left|\widetilde{B}_{l}^{t-1}-\bar{B}_{m,l}^{t-1}\right|}{||\widetilde{B}_{l}^{t-1}-\bar{B}_{m,l}^{t-1}||_{2}})(\bar{A}_{m,l}^{t-1}+\alpha\frac{\left|\widetilde{A}_{l}^{t-1}-\bar{A}_{m,l}^{t-1}\right|}{||\widetilde{A}_{l}^{t-1}-\bar{A}_{m,l}^{t-1}||_{2}})\right\}_{\in\Psi_{t-1}}
$$
