$$
\begin{array}{r}{\widehat{\tau}^{(t)}(a,x,s)=\frac{\sum_{l=1}^{t-1}\sum_{i=1}^{n_{l}}Y_{i l}\mathbb{I}_{(D_{i s}\leq t-1)}\mathbb{1}_{(A_{i l}=a)}\mathbb{1}_{(X_{i l}=x)}\mathbb{1}_{(S_{i l}=s)}}{\sum_{l=1}^{t-1}\sum_{i=1}^{n_{l}}\mathbb{1}_{(D_{i s}\leq t-1)}\mathbb{1}_{(A_{i l}=a)}\mathbb{1}_{(X_{i l}=x)}\mathbb{1}_{(S_{i l}=s)}},}\\ {\widehat{\tau}^{(t)}(a,x)=\frac{\sum_{l=1}^{t-1}\sum_{i=1}^{n_{l}}Y_{i l}\mathbb{1}_{(D_{i s}\leq t-1)}\mathbb{1}_{(A_{i l}=a)}\mathbb{1}_{(X_{i l}=x)}}{\sum_{l=1}^{t-1}\sum_{i=1}^{n_{l}}\mathbb{1}_{(D_{i s}\leq t-1)}\mathbb{1}_{(A_{i l}=a)}\mathbb{1}_{(X_{i l}=x)}}.}\end{array}
$$

$$
\begin{array}{l}{\displaystyle\widehat{\tau}=\frac{1}{N}\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\left\{\frac{A_{i t}\mathbf{1}_{(D_{i t}\leq T-t)}}{\sum_{t=1}^{T}r_{t}\cdot\widehat{e}_{t}(X_{i t})\widehat{\rho}_{t,1}(D_{i t}\leq T-t|X)}\Big(Y_{i t}-\widehat{\tau}(1,X_{i t},S_{i t})\Big)\right.}\\ {\displaystyle\qquad\qquad\left.-\,\frac{(1-A_{i t})\mathbf{1}_{(D_{i t}\leq T-t)}}{\sum_{t=1}^{T}r_{t}\cdot(1-\widehat{e}_{t}(X_{i t})\widehat{\rho}_{t,0}(D_{i t}\leq T-t|X)}\Big(Y_{i t}-\widehat{\tau}(0,X_{i t},S_{i t})\Big)\right.}\\ {\displaystyle\qquad\qquad\left.+\,\frac{A_{i t}}{\sum_{t=1}^{T}r_{t}\cdot\widehat{e}_{t}(X_{i t})}\Big(\widehat{\tau}(1,X_{i t},S_{i t})-\widehat{\tau}(1,X_{i t})\Big)\right.}\\ {\displaystyle\qquad\qquad\left.-\,\frac{(1-A_{i t})}{\sum_{t=1}^{T}r_{t}\cdot(1-\widehat{e}_{t}(X_{i t})}\Big(\widehat{\tau}(0,X_{i t},S_{i t})-\widehat{\tau}(0,X_{i t})\Big)+\widehat{\tau}(1,X_{i t})-\widehat{\tau}(0,X_{i t})\right\},}\end{array}
$$

$$
\begin{array}{l}{\displaystyle\widetilde{\gamma}=\frac{1}{N}\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\frac{\mathbf{1}}{\{\sum_{t=1}^{T}r_{t}\cdot\hat{\boldsymbol{r}}_{t}(X_{i t})\hat{\rho}_{1}(T-\hat{\tau}(1,X_{i t},S_{i t}))^{2}}}+\frac{\mathbf{1}_{(D_{i t}\leq T-t)}(1-A_{i t})(Y_{i t}-\hat{\tau}(0,X_{i t},S_{i t}))^{2}}{\{\sum_{t=1}^{T}r_{t}\cdot\big(1-\hat{\epsilon}_{t}(X_{i t})\big)\hat{\rho}_{0}(T-t|X_{i t})\}^{2}}}\\ {\displaystyle\quad+\,\frac{1}{N}\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\frac{A_{i t}(\hat{\tau}(1,X_{i t},S_{i t})-\hat{\tau}(1,X_{i t}))^{2}}{\{\sum_{t=1}^{T}r_{t}\cdot\hat{e}_{t}(X_{i t})\}^{2}}+\frac{(1-A_{i t})\big(\hat{\tau}(0,X_{i t},S_{i t})-\hat{\tau}(0,X_{i t})\big)^{2}}{\{\sum_{t=1}^{T}r_{t}\cdot\big(1-\hat{e}_{t}(X_{i t})\big)\}^{2}}}\\ {\displaystyle\quad+\,\frac{1}{N}\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\big(\hat{\tau}(1,X_{i t})-\hat{\tau}(0,X_{i t})-\hat{\tau}\big)^{2}.}\end{array}
$$

$$
\begin{array}{r l}&{~\mathbb E\Bigg[\bigg(A\displaystyle\sum_{t=1}^{T}\mathbb{1}_{(V=t)}\mathbb{1}_{D\leq T-t}\bigg)\mathfrak f(Y|X,S)\Bigg)\times\bigg(A\displaystyle\sum_{t=1}^{T}\mathbb{1}_{(V=t)}\mathbb{1}_{D\leq T-t}\bigg)\mathfrak f(Y|X,S)\Bigg]}\\ &{=\mathbb E\Bigg[\bigg(A\displaystyle\sum_{t=1}^{T}\mathbb{1}_{(V=t)}\mathbb{1}_{D\leq T-t}\bigg)\mathfrak f(Y|X,S)^{2}\Bigg]}\\ &{=\mathbb E\Bigg[\mathbb E\bigg[\bigg(A\displaystyle\sum_{t=1}^{T}\mathbb{1}_{(V=t)}\mathbb{1}_{D\leq T-t}\bigg)\mathfrak f(Y|X,S)^{2}\lvert X\rvert\bigg]}\\ &{=\mathbb E\displaystyle\left[\sum_{t=1}^{T}e_{t}(X)r_{t}\rho_{1}(T-t|X)\mathbb{E}\Big[\mathfrak f(Y|X,S)^{2}\lvert X\rvert\bigg].}\end{array}
