$$
\begin{array}{r l}&{\widehat{r}_{1}=\cfrac{1}{N}\displaystyle\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\left\{\cfrac{A_{i t}\mathbf{1}_{(D_{i t}\leq T-t)}}{\sum_{t=1}^{T}r_{t}\cdot\hat{e}_{t}(X_{i t})\hat{\rho}_{t,1}(D_{i t}\leq T-t|X)}\Big(Y_{i t}-\hat{\tau}(1,X_{i t},S_{i t})\Big)\right.}\\ &{\qquad\qquad\qquad\left.+\cfrac{A_{i t}}{\sum_{t=1}^{T}r_{t}\cdot\hat{e}_{t}(X_{i t})}\Big(\hat{\tau}(1,X_{i t},S_{i t})-\hat{\tau}(1,X_{i t})\Big)+\hat{\tau}(1,X_{i t})\right\},}\\ &{\widehat{r}_{0}=\cfrac{1}{N}\displaystyle\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\left\{\cfrac{(1-A_{i t})\mathbf{1}_{(D_{i t}\leq T-t)}}{\sum_{t=1}^{T}r_{t}\cdot(1-\hat{e}_{t}(X_{i t}))\hat{\rho}_{t,0}(D_{i t}\leq T-t|X)}\Big(Y_{i t}-\hat{\tau}(0,X_{i t},S_{i t})\Big)\right.}\\ &{\qquad\qquad\qquad\left.+\cfrac{(1-A_{i t})}{\sum_{t=1}^{T}r_{t}\cdot(1-\hat{e}_{t}(X_{i t}))}\Big(\hat{\tau}(0,X_{i t},S_{i t})-\hat{\tau}(0,X_{i t})\Big)+\hat{\tau}(0,X_{i t})\right\}.}\end{array}
