$$
\begin{array}{r l}&{\frac{1}{\mathrm{V}}\displaystyle\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\Biggl\{\frac{A_{i}\mathrm{I}_{(D_{t}\leq T-t)}}{\sum_{t=1}^{T}r_{t}\cdot\hat{e}_{t}(X_{t t})\hat{\rho}_{t}(D_{t i}\leq T-t|X)}\Bigl(Y_{t i}-\tau(1,X_{i t},S_{u})\Bigr)}\\ &{+\underbrace{\left(\frac{A_{i}\mathrm{I}_{(D_{t}\leq T-t)}}{\sum_{t=1}^{T}r_{t}\cdot\hat{e}_{t}(X_{t t})\hat{\rho}_{t}(D_{t i}\leq T-t|X)}-1\right)\Bigl(\tau(1,X_{i t},S_{u})-\hat{\tau}(1,X_{i t},S_{u})\Bigr)}_{(A,2)}}\\ &{+\underbrace{\frac{A_{i}}{\sum_{t=1}^{T}r_{t}\cdot\hat{e}_{t}(X_{t t})}\Bigl(\tau(1,X_{i t},S_{u})-\tau(1,X_{i t})\Bigr)}_{(B,1)}}\\ &{+\left(\frac{A_{i}}{\sum_{t=1}^{T}r_{t}\cdot\hat{e}_{t}(X_{t t})}-1\right)\Bigl(\hat{\tau}(1,X_{i t},S_{u})-\tau(1,X_{i t},S_{u})-\hat{\tau}(1,X_{i t})\Bigr)}\end{array}
$$

$$
\begin{array}{c}{\displaystyle\sum_{t=1}^{T}r_{t}\widehat{e}_{t}(X_{i t})\widehat{\rho}_{1}(D_{i t}\leq T-t|X)=\displaystyle\sum_{t=1}^{T}r_{t}\epsilon_{t}(X_{i t})\rho_{1}(D_{i t}\leq T-t|X)+o_{p}(1),}\\ {\displaystyle\sum_{t=1}^{T}r_{t}(1-\widehat{e}_{t}(X_{i t}))\widehat{\rho}_{0}(D_{i t}\leq T-t|X)=\displaystyle\sum_{t=1}^{T}r_{t}(1-\epsilon_{t}(X_{i t}))\rho_{0}(D_{i t}\leq T-t|X)+o_{p}(1),}\\ {\displaystyle\sum_{t=1}^{T}r_{t}\widehat{e}_{t}(X_{i t})=\displaystyle\sum_{t=1}^{T}r_{t}\epsilon_{t}(X_{i t})+o_{p}(1),}\\ {\displaystyle\sum_{t=1}^{T}r_{t}(1-\widehat{e}_{t}(X_{i t}))=\displaystyle\sum_{t=1}^{T}r_{t}(1-\epsilon_{t}(X_{i t}))+o_{p}(1).}\end{array}
