$$
\begin{array}{r l}&{\displaystyle\frac{1}{N^{2}\mathbb{V}(\mathfrak{e})^{2}}\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\mathbb{E}\left\{\left|\frac{A_{i t}}{\sum_{t=1}^{T}r_{t}\cdot\mathfrak{e}_{t}(X_{i t})}\Big(\tau(1,X_{i t},S_{i t})-\tau(1,X_{i t})\Big)\right|^{4}\right\}\leq\frac{C M_{4}}{N\mathbb{V}(\mathfrak{e})^{2}\delta^{3}},}\\ &{\displaystyle\frac{1}{N^{2}\mathbb{V}(\mathfrak{e})^{2}}\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\mathbb{E}\left\{\left|\frac{1-A_{i t}}{\sum_{t=1}^{T}r_{t}\cdot(1-\mathfrak{e}_{t}(X_{i t}))}\Big(\tau(0,X_{i t},S_{i t})-\tau(0,X_{i t})\Big)\right|^{4}\right\}\leq\frac{C M_{4}}{N\mathbb{V}(\mathfrak{e})^{2}\delta^{3}},}\\ &{\displaystyle\qquad\qquad\frac{1}{N^{2}\mathbb{V}(\mathfrak{e})^{2}}\sum_{t=1}^{T}\sum_{i=1}^{n_{t}}\mathbb{E}\left\{|\tau(1,X_{i t})-\tau(0,X_{i t})-\tau|^{4}\right\}\leq\frac{C M_{4}}{N\mathbb{V}(\mathfrak{e})^{2}}.}\end{array}
