$$
\begin{array}{l}{P(y=c|x_{i})}\\ {=\displaystyle\int\rho(y=c|p_{i})\rho(p_{i}|x_{i})d p_{i}}\\ {=\displaystyle\int p_{i c}\cdot\rho(p_{i}|x_{i})d p_{i}}\\ {=\displaystyle\int\int\cdots\int p_{i c}\cdot\rho(p_{i1},p_{i2,\ldots,p_{i K}}|x_{i})d p_{i1}d p_{i2}\cdots d p_{i K}}\\ {=\displaystyle\int p_{i c}\cdot\rho(p_{i c}|x_{i})d p_{i c},}\end{array}
$$

$$
\begin{array}{r l}&{P(y=c|x_{t})=\int p_{t}\cdot\rho(p_{t}|x_{t})d p_{t}}\\ &{=\int p_{t}\cdot\left[\frac{1}{\beta(\alpha_{t},\,\alpha_{t}=0)}p_{t}^{b_{t}\alpha_{t}-1}(1-p_{t})^{\alpha_{t}-\alpha_{t}-1}\right]d p_{t}}\\ &{=\frac{B(\alpha_{t}=1)_{\varepsilon_{t}}(3,\,\alpha_{t}=0)}{B(\alpha_{t},\,\alpha_{t}=0)}}\\ &{\int\frac{1}{\beta(\alpha_{t},\,\beta_{t}=0)}\frac{1}{\beta(\alpha_{t},\,\alpha_{t}=0)}p_{t}^{b_{t}(1-p_{t})\alpha_{t}-\alpha_{t}-1}d p_{t}}\\ &{=\frac{B(\alpha_{t}=1)_{\varepsilon_{t}}(3,\,\alpha_{t}=0)}{B(\alpha_{t},\,\alpha_{t}=0)}\cdot1}\\ &{=\frac{\Gamma(\alpha_{t}+1)\Gamma(\alpha_{t})}{\Gamma(\alpha_{t}+1)\Gamma(\alpha_{t})}}\\ &{=\frac{\alpha_{t}\Gamma(\alpha_{t})\Gamma(\alpha_{t})}{\Gamma(\alpha_{t})\Gamma(\alpha_{t})}=\frac{\alpha_{t}}{\sum_{k=1}^{K}\alpha_{t}}=\frac{C_{\varepsilon}(G(x_{t}))}{\sum_{k=1}^{K}C_{\varepsilon}(G(x_{t}))}}\\ &{=\mathrm{g}_{t}[\rho(p_{t})](\alpha_{t})}\end{array}
$$

$$
\begin{array}{r l}&{\mathbb{E}_{\rho}(\mathcal{H})\left[\lambda(L(\mathcal{H})-\hat{L}(\mathcal{H},S_{m}))\right]=\lambda\left(\mathbb{E}_{\rho(\mathcal{H})}[L(\mathcal{H})]-\mathbb{E}_{\rho(\mathcal{H})}[\hat{L}(\mathcal{H},S_{m})]\right)}\\ &{\qquad\qquad\qquad\qquad\leq K L(\rho||\pi)+\log\mathbb{E}_{\pi(\mathcal{H})}[e^{\lambda(L(\mathcal{H})-\hat{L}(\mathcal{H},S_{m}))}].}\end{array}
$$
