\operatorname*{min}_{\theta_{1}\geq0,\theta_{2}\geq0}\left\{\theta_{1}+\theta_{2}:\theta_{1}{\tilde{B}}+\theta_{2}{\tilde{C}}\succeq{\tilde{A}}\right\}=\operatorname*{min}_{\theta_{1}\geq0,\theta_{2}\geq0}\left\{\theta_{1}+\theta_{2}:\left(\frac{\theta_{1}B}{-2}\right.\right.\left.\left.\frac{A}{-2}\right)\succeq0\right\}
$$

$$
\begin{array}{r l}&{\underset{\theta_{1}\geq0,\theta_{2}\geq0}{\operatorname*{min}}\left\{\theta_{1}+\theta_{2}:4\theta_{1}\theta_{2}B\succeq A C^{\dagger}A^{\top}\right\}}\\ &{=\underset{\theta_{1}\geq0,\theta_{2}\geq0}{\operatorname*{min}}\left\{\theta_{1}+\theta_{2}:4\theta_{1}\theta_{2}\geq\sigma_{\operatorname*{max}}^{2}\left(\sqrt{B^{\dagger}}A\sqrt{C^{\dagger}}\right)\right\}=\sigma_{\operatorname*{max}}\left(\sqrt{B^{\dagger}}A\sqrt{C^{\dagger}}\right),}\end{array}
$$

$$
\begin{array}{r l}&{4\theta_{1}\theta_{2}B\succeq A C^{\dagger}A^{\top}\Longleftrightarrow4\theta_{1}\theta_{2}I\succeq\sqrt{\Lambda^{-1}}Q^{\top}A C^{\dagger}A^{\top}Q\sqrt{\Lambda^{-1}}}\\ &{\iff4\theta_{1}\theta_{2}\geq\lambda_{\operatorname*{max}}\left(\sqrt{\Lambda^{-1}}Q^{\top}A C^{\dagger}A^{\top}Q\sqrt{\Lambda^{-1}}\right)}\\ &{\iff4\theta_{1}\theta_{2}\geq\lambda_{\operatorname*{max}}\left(\sqrt{C^{\dagger}}A^{\top}B^{\dagger}A\sqrt{C^{\dagger}}\right)\Longleftrightarrow4\theta_{1}\theta_{2}\geq\sigma_{\operatorname*{max}}^{2}\left(\sqrt{B^{\dagger}}A\sqrt{C^{\dagger}}\right),}\end{array}
$$

$$
\begin{array}{r l}&{\underset{x\in\mathbb{R}^{|S_{1}|},y\in\mathbb{R}^{|S_{2}|}}{\operatorname*{max}}\left\{x^{\top}A_{S_{1},S_{2}}y:x^{\top}B_{S_{1},S_{1}}x\leq1,y^{\top}C_{S_{2},S_{2}}y\leq1\right\}}\\ &{=\sigma_{\operatorname*{max}}\left(\sqrt{(B_{S_{1},S_{1}})^{\dagger}}A_{S_{1},S_{2}}\sqrt{(C_{S_{2},S_{2}})^{\dagger}}\right).}\end{array}
$$

$$
\begin{array}{r}{\pmb{q}_{i}^{\top}\bigg(\sum_{j\in[n-r]}q_{j}q_{j}^{\top}\pmb{A}\bigg)\pmb{A}=\pmb{q}_{i}^{\top}\pmb{0}\Longrightarrow q_{i}^{\top}\pmb{A}=\mathbf{0},}\end{array}
$$

$$
\begin{array}{r l}&{\underset{x\in\mathbb{R}^{|S|}}{\operatorname*{max}}\{a_{S}^{\top}x:x^{\top}B_{S,S}x\leq1\}=\underset{\mu\geq0}{\operatorname*{min}}\ \underset{x\in\mathbb{R}^{|S|}}{\operatorname*{max}}\,a_{S}^{\top}x+\mu-\mu x^{\top}B_{S,S}x}\\ &{\qquad\qquad\qquad\qquad\qquad\qquad=\underset{\mu\geq0}{\operatorname*{min}}\,\mu+\frac{a_{S}^{\top}B_{S,S}^{\dagger}a_{S}}{4\mu}=\sqrt{a_{S}^{\top}(B_{S,S})^{\dagger}a_{S}},}\end{array}
$$

$$
\begin{array}{r l}&{v_{x}\leq\underset{\lambda\in\mathbb{R}_{+}}{\operatorname*{min}}\ \underset{x\in\mathbb{R}^{n}}{\operatorname*{max}}\lbrace a^{\top}x+\lambda-\lambda x^{\top}B x:\|x\|_{0}\leq s_{1}\rbrace}\\ &{\quad=\underset{\lambda\in\mathbb{R}_{+}}{\operatorname*{min}}\ \underset{S\subseteq[n],\lfloor S\rfloor\leq s}{\operatorname*{max}}\lambda+\frac{a_{S}^{\top}(B_{S,S})^{\dagger}a_{S}}{4\lambda}}\\ &{\quad\leq\underset{S\subseteq[n],\lfloor S\rfloor\leq s}{\operatorname*{max}}\lambda^{*}+\frac{a_{S}^{\top}(B_{S,S})^{\dagger}a_{S}}{4\lambda^{*}}=\sqrt{a_{S^{*}}^{\top}(B_{S^{*},S^{*}})^{\dagger}a_{S^{*}}}\leq v_{x},}\end{array}
$$

$$
\begin{array}{r}{\pmb{a}_{S}-\pmb{B}_{S,S}\pmb{B}_{S,S}^{\dagger}\pmb{a}_{S}=\mathbf{0},\forall S\subseteq[n].}\end{array}
$$

$$
\begin{array}{r l}&{v^{*}=\underset{z\in\mathcal{Z}}{\operatorname*{max}}\ \underset{{X\in\mathcal{S}_{+}^{n+m}}}{\operatorname*{max}}\,\big\{\,\mathrm{tr}(\tilde{A}X):\mathrm{tr}(\tilde{B}X)\leq1,\mathrm{tr}(\tilde{C}X)\leq1,\qquad}\\ &{\qquad\qquad\qquad\qquad\qquad X_{i i}(1-z_{i})=0,\forall i\in[m+n]\big\}.}\end{array}
$$

$$
\begin{array}{l}{\displaystyle f(z)=\operatorname*{min}_{\theta_{1}\geq0,\theta_{2}\geq0,\,x\in S_{+}^{n+m}}\mathrm{tr}(\tilde{A}X)-\theta_{1}\,\mathrm{tr}(\tilde{B}X)-\theta_{2}\,\mathrm{tr}(\tilde{C}X)+\theta_{1}+\theta_{2},}\\ {\displaystyle\quad-\sum_{i\in[n+m]}\lambda_{i}X_{i i}+\sum_{i\in[n+m]}\lambda_{i}M_{i i}z_{i}}\\ {\displaystyle\qquad=\operatorname*{min}_{\theta_{1}\geq0,\theta_{2}\geq0,\atop i\in\mathbb{R}_{+}^{n+m}}\bigg\{\theta_{1}+\theta_{2}+\sum_{i\in[n+m]}\lambda_{i}M_{i i}z_{i}:\bigg(\begin{array}{c c}{\theta_{1}B}&{-A/2}\\ {-A^{\top}/2}&{\theta_{2}C}\end{array}\bigg)\succeq-\mathrm{Diag}(\lambda)\bigg\},}\end{array}
$$

$$
\begin{array}{r l}&{v^{*}=\underset{z\in\mathcal{Z},v}{\operatorname*{max}}\;\bigg\lbrace v:v\le\sigma_{\operatorname*{max}}\left(\sqrt{(B_{S_{1},S_{1}})^{\dagger}}A_{S_{1},S_{2}}\sqrt{(C_{S_{2},S_{2}})^{\dagger}}\right)+}\\ &{\quad\quad\quad\displaystyle\sum_{i\in S_{1}\cup(S_{2}+n)}\lambda^{*}M_{i i}z_{i}:\forall S_{1}\subseteq[n],|S_{1}|\le s_{1},S_{2}\subseteq[m],|S_{2}|\le s_{2}\bigg\rbrace,}\end{array}
$$

$$
\begin{array}{r}{D_{3}=\left(\begin{array}{r l}{\theta_{1}^{*}B_{[n]\backslash S_{1},[n]\backslash S_{1}}}&{-A_{[n]\backslash S_{1},[m]\backslash S_{2}}/2}\\ {-A_{[n]\backslash S_{1},[m]\backslash S_{2}}^{\top}/2}&{\theta_{2}^{*}C_{[m]\backslash S_{2},[m]\backslash S_{2}}}\end{array}\right),}\\ {\jmath_{1}^{*}=\theta_{2}^{*}=\sigma_{\operatorname*{max}}\left(\sqrt{(B_{S_{1},S_{1}})^{\dagger}}A_{S_{1},S_{2}}\sqrt{(C_{S_{2},S_{2}})^{\dagger}}\right)/2.}\end{array}
$$

$$
\begin{array}{r l}&{\underset{{\boldsymbol x}\in S_{+}^{n+m}}{\operatorname*{max}}\{\mathrm{tr}(\tilde{A}{\boldsymbol X}):\mathrm{tr}(\tilde{B}{\boldsymbol X})\le1,\mathrm{tr}(\tilde{C}{\boldsymbol X})\le1,X_{i i}=0,\forall i\in[n+m]\setminus T\}}\\ &{=\sigma_{\operatorname*{max}}\left(\sqrt{(B_{S_{1},S_{1}})^{\dagger}}A_{S_{1},S_{2}}\sqrt{(C_{S_{2},S_{2}})^{\dagger}}\right)\ge f(\ensuremath{z}),}\end{array}
$$

