$$
\begin{array}{c l}{{<K_{r}({\bf a}),K_{r}({\bf b})>=\lbrack a_{1}{\bf a},\cdot\cdot,a_{d}{\bf a}\rbrack\cdot\lbrack b_{1}{\bf b},\cdot\cdot,b_{d}{\bf b}\rbrack}}\\ {{\displaystyle=\lbrack a_{1}a_{1},\cdot\cdot,a_{1}a_{d},a_{2}a_{1},\cdot\cdot,a_{2}a_{d},\cdot\cdot,a_{d}a_{1},\cdot\cdot,{\bf\cdot},{\bf\cdot},{\bf},a_{d}a_{d}\rbrack}}\\ {{\displaystyle\quad\cdot\left[b_{1}b_{1},\cdot\cdot,b_{1}b_{d},b_{2}b_{1},\cdot\cdot,b_{2}b_{d},\cdot\cdot,b_{d}b_{1},\cdot\cdot,b_{d}b_{d}\right]}}\\ {{\displaystyle=\sum_{i=1}^{d}a_{i}^{2}b_{i}^{2}+2\sum_{i=1}^{d-1}\sum_{j=i+1}^{d}a_{i}b_{i}a_{j}b_{j}}}\\ {{\displaystyle=<{\bf a},{\bf b}>^{2}.}}\end{array}
$$

$$
\begin{array}{l}{\mathrm{Sim}(\mathbf{q},\mathbf{k})\approx\frac{<\phi(\mathbf{q}),\,\phi(\mathbf{k})>^{2}+1}{2}\,}\\ {=\frac{<K_{r}(\phi(\mathbf{q}),\,K_{r}(\phi(\mathbf{k}))>\,+1}{2}}\\ {=<\varphi(\mathbf{q}),\varphi(\mathbf{k})>,}\end{array}
$$

$$
\begin{array}{l}{{K_{r}(\phi(\mathbf{x}))=K_{r}([\frac{\mathbf{x}}{\sqrt{d}},1])}}\\ {{\ }}\\ {{\ }\qquad=\left[\frac{x_{1}}{\sqrt[4]{d}}\cdot[\frac{\mathbf{x}}{\sqrt[4]{d}},1],\ \cdots,\ \frac{x_{d}}{\sqrt[4]{d}}\cdot[\frac{\mathbf{x}}{\sqrt{d}},1],\ [\frac{\mathbf{x}}{\sqrt[4]{d}},1]\right]}}\\ {{\ }\qquad=\left[\{\frac{x_{1}x_{1}}{\sqrt{d}},\cdots,\frac{x_{1}x_{d}}{\sqrt{d}},\frac{x_{1}}{\sqrt[4]{d}}\},\cdots,\{\frac{x_{d}x_{1}}{\sqrt{d}},\cdots,\frac{x_{d}x_{d}}{\sqrt{d}},\frac{x_{d}}{\sqrt[4]{d}}\},\{\frac{x_{1}}{\sqrt[4]{d}},\cdots,\frac{x_{d}}{\sqrt{d}},1\}\right].}}\end{array}
$$

