$$
\begin{array}{r l}&{p(\{t_{i}\}_{i=\tau_{m}}^{m}|\mathbf{w},\bar{\lambda})=\displaystyle\prod_{i=\tau_{m}}^{m}\bar{\lambda}\sigma(h(t_{i}))\exp\left(-\displaystyle\int_{t_{\tau_{m}}}^{t_{m}}\bar{\lambda}\sigma(h(t))d t\right)}\\ &{\qquad\qquad\qquad\qquad=\displaystyle\prod_{i=\tau_{m}}^{m}\left(\int_{0}^{\infty}\bar{\lambda}e^{f(\omega_{i},h(t_{i}))}p\mathrm{Pe}(\omega_{i}|1,0)d\omega_{i}\right)\mathbb{E}_{p_{\lambda}}\left[\displaystyle\prod_{(\omega,t)\in\Pi}e^{f(\omega,-h(t))}\right]}\\ &{\qquad\qquad\qquad=\displaystyle\int\int\prod_{i=\tau_{m}}^{m}[\lambda(t_{i},\omega_{i})e^{f(\omega_{i},h(t_{i}))}]p_{\lambda}(\Pi|\bar{\lambda})\prod_{(\omega,t)\in\Pi}e^{f(\omega,-h(t))}d\omega d\Pi,}\end{array}
$$

$$
\begin{array}{r l}&{p(\Pi|\{t_{i}\}_{i=\tau_{m}}^{m},\mathbf{w},\bar{\lambda})=\frac{p_{\lambda}(\Pi|\bar{\lambda})\prod_{(\omega,t)\in\Pi}e^{f(\omega,-h(t))}}{\exp\Big(-\int_{t_{\tau_{m}}}^{t_{m}}\int_{0}^{\infty}(1-e^{f(\omega,-h(t))})\bar{\lambda}p_{\mathrm{PG}}(\omega|1,0)d\omega d t)\Big)}}\\ &{=\displaystyle\prod_{(\omega,t)\in\Pi}\Big(e^{f(\omega,-h(t))})\bar{\lambda}p_{\mathrm{PG}}(\omega|1,0)\Big)\exp\left(-\int_{t_{\tau_{m}}}^{t_{m}}\int_{0}^{\infty}e^{f(\omega,-h(t))}\bar{\lambda}p_{\mathrm{PG}}(\omega|1,0)d\omega d t\right).}\end{array}
