$$
\bar{\mathbf{q}}_{i j}=\bar{W}^{Q}M_{i j},\;\bar{\mathbf{k}}_{i j}=\bar{W}^{K}M_{i j},\;\bar{\mathbf{v}}_{i j}=\bar{W}^{V}M_{i j}\Rightarrow\bar{M}_{i j}=\sum_{i^{\prime}=1}^{N}\frac{\exp(\bar{\mathbf{q}}_{i j}\cdot\bar{\mathbf{k}}_{i^{\prime}j})}{\sum_{l=1}^{N}\exp(\bar{\mathbf{q}}_{i j}\cdot\bar{\mathbf{k}}_{l j})}\overline{{\mathbf{v}}}_{i^{\prime}j}
$$

$$
\mathbf{q}_{i j}=W^{Q}\bar{M}_{i j},\ \mathbf{k}_{i j}=W^{K}\bar{M}_{i j},\ \mathbf{v}_{i j}=W^{V}\bar{M}_{i j}\Rightarrow F(M)_{i j}=\sum_{j^{\prime}=1}^{P}\frac{\exp(\mathbf{q}_{i j}\cdot\mathbf{k}_{i j^{\prime}})}{\sum_{l=1}^{P}\exp(\mathbf{q}_{i j}\cdot\mathbf{k}_{i l})}\mathbf{v}_{i j^{\prime}}
$$

$$
\mathcal{L}=\lambda_{\mathrm{Reproject}}\mathcal{L}_{\mathrm{Reproject}}+\lambda_{\mathrm{Staic}}\mathcal{L}_{\mathrm{Staic}}+\lambda_{\mathrm{Negative}}\mathcal{L}_{\mathrm{Negative}}+\lambda_{\mathrm{Sparse}}\mathcal{L}_{\mathrm{Sparse}}
$$
