$$

$$
\begin{array}{l}{\displaystyle\hat{p}_{\sigma}(\tilde{x}_{i}|s_{i})=\left[\hat{p}(\cdot|s_{i})*\!N(\cdot;0,\sigma^{2}I)\right](\tilde{x}_{i})=\int_{\mathbb{R}^{F}}\hat{p}(x_{0}|s_{i})\!\!N(x_{0}-\tilde{x}_{i};0,\sigma^{2}I)d x_{0}\qquad\qquad(1<i\neq j)}\\ {\displaystyle=\frac{9}{M}\sum_{m=1}^{M}p(s_{i}|m)\int_{\mathbb{R}^{F}}\delta(x_{0}-x_{m})\!\!N(x_{0};\tilde{x}_{i},\sigma^{2}I)d x_{0}=\frac{9}{M}\sum_{m=1}^{M}p(s_{i}|m)\!\!N(x_{m};\tilde{x}_{i},\sigma^{2}I)\,,}\end{array}
$$

$$
\begin{array}{l}{\displaystyle\mathbb{E}\left[\|D(\tilde{x}_{i};\sigma,\pi)-x_{i}\|^{2}\right]}\\ {\displaystyle=\mathbb{E}_{p(\pi)}\left[\frac{9}{M}\sum_{s=1}^{S}p(s|\pi^{(i)})\int_{\mathbb{R}^{F}}\sum_{m=1}^{M}p(s|m)\mathcal{N}(x_{m};\tilde{x}_{i},\sigma^{2}I)\|D(\tilde{x}_{i};\sigma,\pi)-x_{m}\|^{2}d\tilde{x}_{i}\right]}\\ {\displaystyle=\mathbb{E}_{p(\pi)}\left[\frac{9}{M}\int_{\mathbb{R}^{F}}\sum_{\underbrace{m=1}_{s=1}}^{M}p(s|\pi^{(i)})p(s|m)\mathcal{N}(\tilde{x}_{i};x_{m},\sigma^{2}I)\|D(\tilde{x}_{i};\sigma,\pi)-x_{m}\|^{2}\,d\tilde{x}_{i}\right]}\end{array}
$$

$$
D^{*}(\tilde{x}_{i};\sigma,\pi)=\sum_{m=1}^{M}x_{m}\frac{\mathcal{N}(\tilde{x}_{i},x_{m},\sigma^{2})\sum_{s=1}^{S}p(s|\pi^{(i)})p(s|m)}{\sum_{m^{\prime}=1}^{M}\mathcal{N}(\tilde{x}_{i};x_{m^{\prime}},\sigma^{2})\sum_{s=1}^{S}p(s|\pi^{(i)})p(s|m^{\prime})}
$$

$$
D^{*}(\tilde{x}_{i};\sigma,\pi)=\sum_{j\in\mathcal{N}(i)}x_{j}\frac{\exp\left(-\frac{1}{2\sigma^{2}}\|\tilde{x}_{i}-x_{j}\|\right)\sum_{s=1}^{S}\pi^{(i,s)}\pi^{(j,s)}}{\sum_{j^{\prime}\in\mathcal{N}(i)}\exp\left(-\frac{1}{2\sigma^{2}}\|\tilde{x}_{i}-x_{j^{\prime}}\|\right)\sum_{s=1}^{S}\pi^{(i,s)}\pi^{(j^{\prime},s)}}
$$

