$$
{\pmb Q}_{t}(i,j)=\frac{1}{2b}e^{-\frac{|d_{t}(i,j)|}{b}},\quad\mathrm{where~}d_{t}(i,j)=\frac{|i/N-j/M|}{\sqrt{1/N^{2}+1/M^{2}}}
$$

$$
\begin{array}{r}{\pmb{Q}(i,j)=\phi\,\pmb{Q}_{t}(i,j)+(1-\phi)\,\pmb{Q}_{o}(i,j),}\end{array}
$$

$$
M_{o}(\hat{\pmb{T}})=\sum_{i=1}^{N+1}\sum_{j=1}^{M+1}\frac{t_{i j}}{\frac{1}{2}d_{m}+1},\quad\mathrm{where~}d_{m}=\left(\frac{i-i_{o}}{N+1}\right)^{2}+\left(\frac{j-j_{o}}{M+1}\right)^{2}
$$

$$
I_{\xi_{1},\xi_{2}}(\alpha,\beta)=\left\{\hat{T}\in\mathbb{R}_{+}^{N+1\times M+1}\mid\hat{T}\mathbf{1}_{M+1}=\alpha,\hat{T}^{\top}\mathbf{1}_{N+1}=\beta,M(\hat{T})\geq\xi_{1},\mathrm{KL}(\hat{T}\parallel\hat{Q})\leq\xi_{2}\right\}
$$

$$
\frac{\partial L(\hat{T},\mu,\nu)}{\partial t_{i j}}=d_{i j}-\lambda_{1}\left(\frac{1}{\left(\frac{i}{N+1}-\frac{j}{M+1}\right)^{2}+1}+\frac{1}{\frac{1}{2}\left(\left(\frac{i-i_{o}}{N+1}\right)^{2}+\left(\frac{j-j_{o}}{M+1}\right)^{2}\right)+1}\right)}\\ {+\,\lambda_{2}\log\frac{t_{i j}}{q_{i j}}+\lambda_{2}+\mu_{i}+\nu_{j}.}\end{array}
$$

$$
\begin{array}{l}{\displaystyle\kappa_{1}\gets\frac{\alpha}{K\kappa_{2}},}\\ {\displaystyle\kappa_{2}\gets\frac{\beta}{K^{T}\kappa_{1}}.}\end{array}
$$

