\begin{array}{r}{f^{*}=\underset{f}{\mathrm{argmin}}\underset{\gamma\in\Delta_{|\mathcal{G}|}}{\mathrm{sup}}\,\mathbb{E}_{(x,y)\sim\sum_{i=1}^{|\mathcal{G}|}\gamma_{i}P_{i}}\big[\ell\big(y,f(x)\big)\big].}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{\mathrm{FFVAE}}(\theta,\phi,\psi)=\mathbb{E}_{p_{\mathrm{data}}(x)}\big[\mathbb{E}_{q_{\phi}(z,t\mid x)}\big[\log p_{\theta}(x\vert z,t)+\alpha\log p_{\psi}(a\vert z)\big]}\\ &{\qquad\qquad\qquad-\mathrm{\bf~KL}(q_{\phi}(z,t\mid x)\|p(z,t))\big]-\gamma\mathrm{\bfKL}(q(z,t)\|q(t)\prod_{j}q(z_{j})),}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{\mathrm{COD}}(\theta,\phi,\psi,\omega)=\mathbb{E}_{p_{\mathrm{daa}}(x)}[\mathbb{E}_{q_{\psi}(z|x)q_{\phi}(t|x)}[\log p_{\theta}(x|z,t)+\alpha\log p_{\omega}(a|z)]}\\ &{\phantom{\mathcal{L}_{\mathrm{COD}}(\theta,\phi,\psi,\omega)}-\gamma\mathrm{KL}(q_{\psi}(z|x)\|p(z))-\mathrm{KL}(q_{\phi}(t|x)\|p(t))].}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{\mathrm{COD}}(\theta,\phi,\psi,\omega)=\mathbb{E}_{p_{\mathrm{dua}}(x)}[\mathbb{E}_{q_{\psi}(z\mid x)q_{\phi}(t\mid x)}[\underbrace{\log p_{\theta}(x\mid z,t)}_{\mathrm{reconstuction~loss}}+\underbrace{\alpha\underbrace{\log p_{\omega}(a\mid z)}_{\mathrm{classitication~loss}}}]]}\\ &{\qquad\qquad\qquad-\left[\gamma\underbrace{I(x;z)+I(x;t)}_{\mathrm{mutual~information}}\right]-\left[\gamma\underbrace{\mathrm{KL}(q_{\psi}(z)||p(z))+\mathrm{KL}(q_{\phi}(t)||p(t))}_{\mathrm{KL~divergence~losses}}\right].}\end{array}
$$

$$
\begin{array}{r}{E L B O(x;q,d)=\log p_{\theta}(x|z,t)-\gamma\mathrm{KL}(q_{\psi}(z|x)\|p(z))-\mathrm{KL}(q_{\phi}(t|x)\|p(t))}\end{array}
$$

$$
\begin{array}{r}{\mathrm{KL}(q(z,t|x)\|p(z,t))=\mathrm{KL}(q_{\psi}(z|x)\|p(z))+\mathrm{KL}(q_{\phi}(t|x)\|p(t)).}\end{array}
$$

$$
\begin{array}{r}{E L B O(x;q,d)=\log p_{d}(x)-\mathrm{KL}(q(z,t|x)||p_{d}(z,t|x))-(\gamma-1)\mathrm{KL}(q_{\psi}(z|x)||p(z))}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{q}(q,d)=\mathbb{E}_{p_{d a}(x)}[\log p_{d}(x)-\mathrm{KL}(q(z,t|x)||p_{d}(z,t|x))-(\gamma-1)\mathrm{KL}(q_{\psi}(z|x)||p(z))]}\\ &{\qquad\qquad-\mathbb{E}_{p_{d}}[\frac{1}{\kappa}\exp(\kappa[\log p_{d}(x)-\mathrm{KL}(q(z,t|x)||p_{d}(z,t|x))-(\gamma-1)\mathrm{KL}(q_{\psi}(z|x)||p(z))])].}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{d}(q^{*}(d),d)=\mathbb{E}_{p_{\mathrm{data}}(x)}[E L B O(x;q^{*}(d),d)]+\eta\mathbb{E}_{p_{d}}[E L B O(x;q^{*}(d),d)]}\\ &{\quad\quad\quad\quad\quad=\mathbb{E}_{p_{\mathrm{data}}(x)}[\log p_{d}(x)]+\eta\mathbb{E}_{p_{d}}[\log p_{d}(x)]}\\ &{\quad\quad\quad\quad\quad=-\mathrm{KL}(p_{\mathrm{data}}\|p_{d})+\mathbb{E}_{p_{\mathrm{data}}}[\log p_{\mathrm{data}}(x)]-\eta\mathbf{H}(p_{d}(x)).}\end{array}
$$
