$$
\operatorname*{max}/\operatorname*{min}O F V=f(x)=x^{\top}Q x=\sum_{i=1}^{n}\sum_{j=1}^{n}q_{i j}x_{i}x_{j}
$$

$$
x_{i}={\left\{\begin{array}{l l}{1,}&{s t a t e_{i}>0}\\ {0,}&{s t a t e_{i}\leq0}\end{array}\right.}
$$

$$
Q=\begin{array}{r}{\left[\!\!\begin{array}{c c c c}{q_{11}}&{q_{12}}&{\ldots}&{q_{1n}}\\ {q_{21}}&{q_{22}}&{}&{q_{2n}}\\ {}&{\vdots}&{\ddots}&{\vdots}\\ {q_{n1}}&{q_{n2}}&{\ldots}&{\phantom{\frac{1}{q_{n n}}}}\end{array}\!\!\right]}\end{array}
$$

$$
f\left(x^{\prime}\right)=\sum_{i=1}^{n}q_{i i}{x_{i}^{\prime}}^{2}+\sum_{i=1}^{n}\sum_{j=1}^{i-1}q_{i j}x_{i}^{\prime}x_{j}^{\prime}+\sum_{i=1}^{n}\sum_{j=i-1}^{n}q_{i j}x_{i}^{\prime}x_{j}^{\prime}
$$

$$
\begin{array}{l}{{O F I(x_{k})=f(x^{\prime})-f(x)=(x_{k}^{\prime}-x_{k})}}\\ {{\displaystyle\left[q_{k k}\left(x_{k}^{\prime}+x_{k}\right)\right.\ \left.+\sum_{i\in N,i\neq k}q_{i k}x_{i}+\sum_{j\in N,j\neq k}q_{k j}x_{j}\right]}}\end{array}
$$

$$
Q_{k}^{s u m}=\sum_{j\in N}q_{k j}x_{j}
$$

$$
O F I(x_{k})=q_{k k}+2\sum_{j\in N,j\neq k}q_{k j}x_{j}
$$

$$
\begin{array}{r l r}{\lefteqn{O F I(x_{k})=q_{k k}+2\sum_{j\in N,j\ne k}q_{k j}x_{j}}}\\ &{}&{=q_{k k}+2\sum_{j\in N,j\ne k}q_{k j}x_{j}+2q_{k k}x_{k}}\\ &{}&{=q_{k k}+2Q_{k}^{s u m}}\end{array}
$$

$$
O F I(x_{k})=-q_{k k}-2\sum_{j\in N,j\neq k}q_{k j}x_{j}
$$

$$
\begin{array}{c}{{O F I(x_{k})=-q_{k k}-2\displaystyle\sum_{j\in N,j\neq k}q_{k j}x_{j}}}\\ {{=q_{k k}-2\displaystyle\sum_{j\in N}q_{k j}x_{j}}}\\ {{=q_{k k}-2Q_{k}^{s u m}}}\end{array}
$$

$$
O F I(x)=Q^{d i a g}+2(1-2x)Q^{s u m}
$$
