\hat{\mathbf{y}}_{i}=(\hat{y}_{i,k})_{1\leq k\leq K}\in\Delta_{K};\quad\hat{y}_{i,k}=\frac{\exp(\tau\mathbf{f}_{i}^{\top}\mathbf{t}_{k})}{\sum_{j}\exp(\tau\mathbf{f}_{i}^{\top}\mathbf{t}_{j})}
$$

$$
\mathcal{L}_{\mathrm{ZER0-Starr}}(\mathbf{z},\mu,\boldsymbol{\Sigma})=\underbrace{-\sum_{i\in\mathcal{Q}}\mathbf{z}_{i}^{\top}\log(\mathbf{p}_{i})}_{\mathrm{GMM\;clustering}}-\underbrace{\sum_{i\in\mathcal{D}}\sum_{j\in\mathcal{D}}w_{i j}\mathbf{z}_{i}^{\top}\mathbf{z}_{j}}_{\mathrm{Laplacian\;reg.}}+\underbrace{\sum_{i\in\mathcal{Q}}\mathrm{KL}_{\lambda}(\mathbf{z}_{i}||\hat{\mathbf{y}}_{i})}_{\mathrm{Text\;knowledge}}
$$

$$
\begin{array}{r}{\mathrm{KL}_{\lambda}(\mathbf{z}_{i}||\hat{\mathbf{y}}_{i})=\mathbf{z}_{i}^{\top}\log\mathbf{z}_{i}-\lambda\mathbf{z}_{i}^{\top}\log\hat{\mathbf{y}}_{i},\quad i\in\mathcal{Q};\quad\lambda>0}\end{array}
$$

$$
\mathbf{z}_{i}^{(l+1)}=\frac{\hat{\mathbf{y}}_{i}^{\lambda}\odot\exp(\log(\mathbf{p}_{i})+\sum_{j\in\mathcal{D}}w_{i j}\mathbf{z}_{j}^{(l)})}{(\hat{\mathbf{y}}_{i}^{\lambda}\odot\exp(\log(\mathbf{p}_{i})+\sum_{j\in\mathcal{D}}w_{i j}\mathbf{z}_{j}^{(l)}))^{\top}\mathbb{1}_{K}}
$$

$$
\pmb{\mu}_{k}=\frac{\frac{\gamma}{|S|}\sum_{i\in S}z_{i,k}\mathbf{f}_{i}+\frac{1}{|Q|}\sum_{i\in Q}z_{i,k}\mathbf{f}_{i}}{\frac{\gamma}{|S|}\sum_{i\in S}z_{i,k}+\frac{1}{|Q|}\sum_{i\in Q}z_{i,k}}
$$

$$
\operatorname{diag}(\Sigma)=\frac{\frac{\gamma}{|S|}\sum_{i\in\mathcal{S}}\sum_{k}z_{i,k}(\mathbf{f}_{i}-\pmb{\mu}_{k})^{2}+\frac{1}{|Q|}\sum_{i\in Q}\sum_{k}z_{i,k}(\mathbf{f}_{i}-\pmb{\mu}_{k})^{2}}{\gamma+1}
$$

$$
\begin{array}{r}{z_{i,k}\leftarrow\frac{\hat{y}_{i,k}^{\lambda}\exp(\log(p_{i,k})+\sum_{j\in\mathcal{D}}w_{i j}z_{j,k})}{\sum_{k^{\prime}}\hat{y}_{i,k^{\prime}}^{\lambda}\exp(\log(p_{i,k^{\prime}})+\sum_{j\in\mathcal{D}}w_{i j}z_{j,k^{\prime}})}\quad\forall\,i\,\forall\,k}\end{array}
$$

