$$

$$
\mathbf{E}_{v}=\mathrm{PointNet}(\mathbf{P}_{v}),~~\mathbf{E}_{v}\in\mathbb{R}^{n\times D}
$$

$$
\begin{array}{r l}&{\mathbf{P}\mathbf{E}_{x}=\left[\sin\left(\frac{x}{e^{2\times\frac{1}{D/6}}}\right),\cos\left(\frac{x}{e^{2\times\frac{1}{D/6}}}\right),\sin\left(\frac{x}{e^{2\times\frac{2}{D/6}}}\right),\right.}\\ &{\qquad\quad\left.\cos\left(\frac{x}{e^{2\times\frac{2}{D/6}}}\right),\dots,\sin\left(\frac{x}{e^{2}}\right),\cos\left(\frac{x}{e^{2}}\right)\right]}\end{array}
$$

$$
\begin{array}{r l r}&{\mathbf{PE}_{v}^{s i n-c o s}=\operatorname{sin-cos}\mathrm{~PE}(\mathbf{C}_{v}),\;\;\mathbf{PE}_{v}^{s i n-c o s}\in\mathbb{R}^{(1-m)n\times D}}&\\ &{\mathbf{PE}_{m}^{s i n-c o s}=\operatorname{sin-cos}\mathrm{~PE}(\mathbf{C}_{m}),\;\;\mathbf{PE}_{m}^{s i n-c o s}\in\mathbb{R}^{m n\times D}}&\\ &{\quad\;\mathbf{PE}_{v}=\mathrm{PEM}(\mathbf{PE}_{v}^{s i n-c o s}),\;\;\mathbf{PE}_{v}\in\mathbb{R}^{(1-m)n\times D}}&\\ &{\quad\;\mathbf{PE}_{m}=\mathrm{PEM}(\mathbf{PE}_{m}^{s i n-c o s}),\;\;\mathbf{PE}_{m}\in\mathbb{R}^{m n\times D}}&\end{array}
$$

$$
{\bf T}_{i}=\mathrm{Attn}({\bf Q}_{v},{\bf K}_{v},{\bf V}_{v})=\mathrm{SoftMax}\left(\frac{{\bf Q}_{v}{\bf K}_{v}}{\sqrt{D}}\right){\bf V}_{v}
$$

$$
\mathbf{P}\mathbf{E}_{m}^{i}=\mathrm{Attn}(\mathbf{Q}_{m},\mathbf{K}_{v+m},\mathbf{V}_{v+m})=\mathrm{SoftMax}\left(\frac{\mathbf{Q}_{m}\mathbf{K}_{v+m}}{\sqrt{D}}\right)\mathbf{V}_{v+m}
$$

$$
\mathbf{P}_{p r e d}=\operatorname{Reshape}(\operatorname{Linear}(\mathbf{H}_{m})),\ \ \mathbf{P}_{p r e d}\in\mathbb{R}^{m n\times k\times3}
$$

$$
\mathcal{L}_{P C}=\frac{1}{m n D}||\mathbf{P}\mathbf{E}_{m}^{p r e d}-\mathbf{P}\mathbf{E}_{m}||_{2}^{2}
$$

$$
\mathcal{L}_{R e c o n}=\frac{1}{|\mathbf{P}_{p r e d}|}\sum_{a\in\mathbf{P}_{p r e d}}\operatorname*{min}_{b\in\mathbf{P}}\|a-b\|_{2}^{2}+\frac{1}{|\mathbf{P}|}\sum_{b\in\mathbf{P}}\operatorname*{min}_{a\in\mathbf{P}_{p r e d}}\|a-b\|_{2}^{2}
$$
