$$
\begin{array}{r l}{\underset{\mathbf{X}_{\mathrm{rest}},\mathbf{X}_{\mathrm{static}}}{\mathrm{min}}\,}&{\mathcal{I}(\mathbf{X}_{\mathrm{rest}},\mathbf{x}_{\mathrm{static}})=\mathcal{L}(\mathbf{x}_{\mathrm{static}})+\mathcal{L}_{\mathrm{reg}}(\mathbf{X}_{\mathrm{rest}})}\\ {\mathrm{s.t.}\,}&{\mathbf{f}_{\mathrm{int}}(\mathbf{x}_{\mathrm{static}},\mathbf{X}_{\mathrm{rest}};\boldsymbol{\Theta})=\mathbf{f}_{\mathrm{ext}}(\mathbf{x}_{\mathrm{static}}).}\end{array}
$$

$$
\begin{array}{r l}{\underset{\mathbf{F}_{\mathrm{p}},\mathbf{x}_{\mathrm{static}}}{\mathrm{min}}\,}&{\mathcal{I}(\mathbf{F}_{\mathbf{p}},\mathbf{x}_{\mathrm{static}})=\mathcal{L}(\mathbf{x}_{\mathrm{static}})+\mathcal{L}_{\mathrm{reg}}(\mathbf{F}_{\mathbf{p}})}\\ {\mathrm{s.t.}\,}&{\mathbf{f}_{\mathrm{int}}(\mathbf{x}_{\mathrm{static}},\phi(\mathbf{F}_{\mathbf{p}};\mathbf{X}_{\mathrm{init}});\boldsymbol{\Theta})=\mathbf{f}_{\mathrm{ext}}(\mathbf{x}_{\mathrm{static}}).}\end{array}
$$

$$
\begin{array}{r l r}&{\mathcal{E}(\mathbf{F_{e}},\mathbf{F_{p}};\boldsymbol{\Theta})=V(\mathbf{F_{p}})\Phi(\mathbf{F_{e}};\boldsymbol{\Theta}),}&\\ &{\quad\quad\quad V(\mathbf{F_{p}})=V_{\mathrm{init}}\mathrm{det}(\mathbf{F_{p}}),}&\\ &{\quad\quad\quad\quad\mathbf{F_{e}}=\mathbf{F}\mathbf{F_{p}}^{-1},}&\\ &{\quad\quad\quad\quad\mathbf{F}=\partial\mathbf{x}/\partial\mathbf{X}_{\mathrm{init}},}\end{array}
$$

$$
\begin{array}{r l}&{\mathbf{f}_{\mathrm{int}}(\mathbf{x},\phi(\mathbf{F_{p}};\mathbf{X}_{\mathrm{init}});\boldsymbol{\Theta}):=\frac{\partial\mathcal{E}\left(\mathbf{F_{e}}(\mathbf{x}),\mathbf{F_{p}};\boldsymbol{\Theta}\right)}{\partial\mathbf{x}}}\\ &{\qquad\qquad\qquad\qquad=V(\mathbf{F_{p}})\frac{\partial\boldsymbol{\Phi}}{\partial\mathbf{F_{e}}}:\frac{\partial\mathbf{F}}{\partial\mathbf{x}}\mathbf{F_{p}}^{-1}.}\end{array}
$$

$$
{\begin{array}{r l}&{{\displaystyle{\frac{\partial{\mathcal{I}}}{\partial\mathbf{F_{p}}}}={\frac{\partial{\mathcal{L}}}{\partial\mathbf{F_{p}}}}+{\frac{\partial{\mathcal{L}}_{\mathrm{reg}}}{\partial\mathbf{F_{p}}}}}\\ &{\qquad={\frac{\partial{\mathcal{L}}}{\partial\mathbf{x}_{\mathrm{static}}}}{\frac{\partial\mathbf{x}_{\mathrm{static}}}{\partial\mathbf{F_{p}}}}+{\frac{\partial{\mathcal{L}}_{\mathrm{reg}}}{\partial\mathbf{F_{p}}}}}\\ &{\qquad=-{\frac{\partial{\mathcal{L}}}{\partial\mathbf{x}_{\mathrm{static}}}}[{\frac{\partial\mathbf{f}_{\mathrm{net}}}{\partial\mathbf{x}_{\mathrm{static}}}}]^{-1}{\frac{\partial\mathbf{f}_{\mathrm{net}}}{\partial\mathbf{F_{p}}}}+{\frac{\partial{\mathcal{L}}_{\mathrm{reg}}}{\partial\mathbf{F_{p}}}},}\end{array}}
$$
