\begin{array}{r l}&{\quad\beta_{X\rightarrow Y}-\omega_{\{\mathbb{G}\backslash G_{j}\}}}\\ &{=\frac{\beta_{X\rightarrow Y}\sum_{k=1}^{m}(A_{X,k}+A_{X,U}\gamma_{U,k})^{2}\mathbf{C}_{k k}-\sum_{k=1}^{m}(A_{X,k}+A_{X,U}\gamma_{U,k})(A_{Y,k}+A_{Y,U}\gamma_{U,k})\mathbf{C}_{k k}}{\sum_{k=1}^{m}(\bar{\beta}_{k})^{2}\mathbf{C}_{k k}}}\\ &{=\frac{(\beta_{X\rightarrow Y}\beta_{Y\rightarrow X}-1)\sum_{k=1}^{m}(A_{X,k}+A_{X,U}\gamma_{U,k})(\gamma_{Y,k}+\gamma_{Y,U}\gamma_{U,k})\mathbf{C}_{k k}}{\sum_{k=1}^{m}(\bar{\beta}_{k})^{2}\mathbf{C}_{k k}}.}\end{array}
$$

$$
\begin{array}{r l}&{\mathrm{corr}(\mathcal{P R}_{(X,Y\mid\mathbb{G}\backslash G_{j})}),G_{j})}\\ &{=\frac{\sum_{k=1}^{m}{\mathbf{C}}_{k k}(A_{X,k}+A_{X,U}\gamma_{U,k})\left(\gamma_{X,k}+\gamma_{X,U}\gamma_{U,k}\right)}{\sum_{k=1}^{m}(\tilde{\beta}_{k})^{2}\mathbf{C}_{k k}}(\gamma_{Y,j}+\gamma_{Y,U}\gamma_{U,j})}\\ &{\ \ \ -\sum_{k=1}^{m}{\mathbf{C}}_{k k}(A_{X,k}+A_{X,U}\gamma_{U,k})(\gamma_{Y,k}+\gamma_{Y,U}\gamma_{U,k})}\\ &{\ \ \ -\frac{\sum_{k=1}^{m}{\mathbf{C}}_{k k}[A_{X,k}+A_{X,U}\gamma_{U,k})\left(\gamma_{Y,k}\right)\left(\gamma_{X,j}+\gamma_{X,U}\gamma_{U,j}\right)}{\sum_{k=1}^{m}(\tilde{\beta}_{k})^{2}\mathbf{C}_{k k}}(\gamma_{X,j}+\gamma_{X,U}\gamma_{U,j})}\\ &{=\frac{\sum_{k=1}^{m}{\tilde{\beta}_{k}}\mathbf{C}_{k k}[\left(\gamma_{X,k}+\gamma_{X,U}\gamma_{U,k}\right)\left(\gamma_{Y,j}+\gamma_{Y,U}\gamma_{U,j}\right)-\left(\gamma_{Y,k}+\gamma_{Y,U}\gamma_{U,k}\right)\left(\gamma_{X,j}+\gamma_{X,U}\gamma_{U,j}\right)]}{\sum_{k=1}^{m}(\tilde{\beta}_{k})^{2}\mathbf{C}_{k k}}}\\ &{=\frac{\sum_{k=1}^{m}{\tilde{\beta}_{k}}\mathbf{C}_{k k}[(\gamma_{X,k}\gamma_{Y,j}-\gamma_{Y,k}\gamma_{X,j})+\gamma_{Y,U}(\gamma_{X,k}\gamma_{U,j}-\gamma_{U,k}\gamma_{X,j})+\gamma_{X,U}(\gamma_{U,k}\gamma_{Y,j}-\gamma_{Y,k}\gamma_{U,j})]}{\sum_{k=1}^{m}(\tilde{\beta}_{k})^{2}\mathbf{C}_{k k}}\neq0.}\end{array}
$$

$$
\begin{array}{r l}&{\ \ \ \mathrm{corr}(\mathcal{P R}_{(X,Y\,|\,\mathbb{G}\backslash G_{j})},G_{j})}\\ &{=(\gamma_{Y,j}+\gamma_{X,j}\beta_{X\rightarrow Y})\Delta-(\gamma_{X,j}+\gamma_{Y,j}\beta_{Y\rightarrow X})\Delta\omega_{\{\mathbb{G}\backslash G_{j}\}}}\\ &{=\Delta\gamma_{X,j}(\beta_{X\rightarrow Y}-\omega_{\{\mathbb{G}\backslash G_{j}\}}).}\end{array}
$$

$$
\begin{array}{r l}&{\quad\beta_{X\rightarrow Y}-\omega_{\{\mathbb{G}\backslash G_{j}\}}}\\ &{=\frac{\beta_{X\rightarrow Y}\sum_{k=1}^{m}\mathbf{C}_{k k}\left[(A_{X,k}+A_{X,U}\gamma_{U,k})^{2}-(A_{X,k}+A_{X,U}\gamma_{U,k})(A_{Y,k}+A_{Y,U}\gamma_{U,k})\right]}{\sum_{k=1}^{m}(\bar{\beta}_{k})^{2}\mathbf{C}_{k k}}}\\ &{=\frac{(\beta_{X\rightarrow Y}\beta_{Y\rightarrow X}-1)\sum_{k=1}^{m}\mathbf{C}_{k k}(A_{X,k}+A_{X,U}\gamma_{U,k})(\gamma_{Y,k}+\gamma_{Y,U}\gamma_{U,k})}{\sum_{k=1}^{m}(\bar{\beta}_{k})^{2}\mathbf{C}_{k k}}.}\end{array}
$$

$$
\begin{array}{r l}&{\mathrm{corr}(\mathscr{P}\mathscr{R}_{(X,Y\,|\,\mathbb{G}\backslash G_{j}),G_{j}})=\Delta\gamma_{X,j}(\beta_{X\rightarrow Y}-\omega_{\{\mathbb{G}\backslash G_{j}\}})}\\ &{=\,-\,\frac{\sum_{k=1}^{m}{\mathbf{C}_{k k}\left(A_{X,k}+A_{X,U}\gamma_{U,k}\right)\left(\gamma_{Y,k}+\gamma_{Y,U}\gamma_{U,k}\right)\gamma_{X,j}}}{\sum_{k=1}^{m}(\overline{{\beta}}_{k})^{2}\mathbf{C}_{k k}}}\\ &{=\,-\,\frac{\sum_{k=1}^{m}\bar{\beta}_{k}\mathbf{C}_{k k}\left(\gamma_{Y,k}+\gamma_{Y,U}\gamma_{U,k}\right)\gamma_{X,j}}{\sum_{k=1}^{m}(\bar{\beta}_{k})^{2}\mathbf{C}_{k k}}.}\end{array}
$$

$$
\begin{array}{r l}{\left|\frac{\mathrm{corr}(G_{j},Y)}{\mathrm{corr}(G_{j},X)}\right|=\left|\frac{\mathrm{Cov}(G_{j},Y)}{\sqrt{\mathrm{Var}(G_{j})\mathrm{Var}(Y)}}\frac{\sqrt{\mathrm{Var}(G_{j})\mathrm{Var}(X)}}{\mathrm{Cov}(G_{j},X)}\right|}\\ {=\left|\frac{\gamma_{Y,j}\Delta}{\sqrt{\mathrm{Var}(Y)}}\frac{\sqrt{\mathrm{Var}(X)}}{\gamma_{Y,j}\Delta\beta_{Y\to X}}\right|}\\ {=\left|\frac{1}{\beta_{Y\to X}}\sqrt{\frac{\mathrm{Var}(X)}{\mathrm{Var}(Y)}}\right|\geq1,}\end{array}
$$

$$
\begin{array}{r l}{\left|\frac{\mathrm{corr}(G_{j},Y)}{\mathrm{corr}(G_{j},X)}\right|=\left|\frac{\mathrm{Cov}(G_{j},Y)}{\sqrt{\mathrm{Var}(G_{j})\mathrm{Var}(Y)}}\frac{\sqrt{\mathrm{Var}(G_{j})\mathrm{Var}(X)}}{\mathrm{Cov}(G_{j},X)}\right|}\\ {=\left|\frac{\gamma_{X,j}\Delta\beta_{X\to Y}}{\sqrt{\mathrm{Var}(Y)}}\frac{\sqrt{\mathrm{Var}(X)}}{\gamma_{X,j}\Delta}\right|}\\ {=\left|\beta_{X\to Y}\sqrt{\frac{\mathrm{Var}(X)}{\mathrm{Var}(Y)}}\right|<1,}\end{array}
$$
