\begin{array}{r l}&{\underset{\epsilon\in[T]}{\sum}\mathbb{E}\left[\underset{n\in[N]}{\sum}(\alpha\mu(n|S_{k_{n-1},n}^{\epsilon},t,\theta_{k_{n-1}}^{*})-\mu(n|S_{k_{n-1},t}^{\alpha},\theta_{k_{n-1}}))Q_{n}(t)1(E_{t}^{1}\cap E_{n,t}^{2})\right]}\\ &{\leq\underset{t\in[T]}{\sum}\mathbb{E}\left[\underset{n\in[N]}{\sum}(\alpha\tilde{\mu}_{t}^{\gamma_{\tilde{U}}}C^{\beta}(n|S_{k_{n-1},t}^{\epsilon},\hat{\theta}_{k_{n-1}}^{*},t)-\mu(n|S_{k_{n-1},t}^{\alpha},\theta_{k_{n-1}}))Q_{n}(t)1(E_{t}^{1}\cap E_{n,t}^{2})\right]}\\ &{\leq\underset{t\in[T]}{\sum}\mathbb{E}\left[\underset{n\in[N]}{\sum}(\tilde{\mu}_{t}^{\gamma_{\tilde{U}}\gamma_{\tilde{U}}}(n|S_{k_{n-1},t}^{\alpha},\hat{\theta}_{k_{n-1},t})-\mu(n|S_{k_{n-1},t}^{\alpha},\theta_{k_{n-1}}))Q_{n}(t)1(E_{t}^{1}\cap E_{n,t}^{2})\right]}\\ &{\leq\underset{t\in[T]}{\sum}\mathbb{E}\left[\underset{n\in[N]}{\sum}2\beta_{t}\big|x_{n}\big|_{V_{n-1}^{\alpha},\tilde{\mu}_{t}}(t)1(E_{t}^{1}\cap E_{n,t}^{2})\right]}\\ &{\leq C_{\epsilon\epsilon}\underset{t\in[T]}{\sum}\sum_{k=1}^{n}\mathbb{E}\left[Q_{n}(t)\big|.}\end{array}
$$

$$
\begin{array}{r l}&{\underset{t\in[T]}{\sum}\mathbb{E}\left[\mathcal{V}(\mathbf{Q}(t+1))-\mathcal{V}(\mathbf{Q}(t))\right]}\\ &{=\underset{t\in[T]}{\sum}\underset{n\in[N]}{\sum}\mathbb{E}\left[\left(Q_{n}(t)+A_{n}(t)-D_{n}(t)\right)^{+2}-Q_{n}(t)^{2}\right]}\\ &{=\underset{t\in[T]}{\sum}\underset{n\in[N]}{\sum}\mathbb{E}\left[\left(Q_{n}(t)+A_{n}(t)-\alpha D_{n}^{*}(t)\right)^{2}-Q_{n}(t)^{2}\right]}\\ &{\quad+\underset{t\in[T]}{\sum}\underset{n\in[N]}{\sum}\mathbb{E}\left[\left(Q_{n}(t)+A_{n}(t)-D_{n}(t)\right)^{+2}-(Q_{n}(t)+A_{n}(t)-\alpha D_{n}^{*}(t))^{2}\right]}\\ &{\leq7\underset{t\in[T]}{\sum}\underset{n\in[N]}{\sum}\underset{n\in[N]}{\sum}\,2\mathrm{eg}[Q_{n}(t)]+2\underset{t\in[T]}{\sum}\underset{n\in[N]}{\sum}\mathbb{E}\left[(\alpha D_{n}^{*}(t)-D_{n}(t))Q_{n}(t)\right],}\end{array}
$$

$$
\begin{array}{r l}{\lefteqn{\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[(\alpha D_{n}^{*}(t)-D_{n}(t)Q_{n}(t)]}}\\ &{\leq\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[(\alpha\mu(n|S_{k_{n,t}^{*},t},\theta_{k_{n,t}^{*}})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))Q_{n}(t)\mathbb{1}(E_{t}^{1}\cap E_{t}^{2}\cap E_{n,t}^{3})]}\\ &{+\displaystyle\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[(\alpha\mu(n|S_{k_{n,t}^{*},t},\theta_{k_{n,t}^{*}})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))Q_{n}(t)(\mathbb{1}((E_{t}^{1})^{c})+\mathbb{1}((E_{t}^{2})^{c})+\mathbb{1}((E_{n,t}^{3})^{c}))]}\end{array}
$$

$$
\begin{array}{r l}&{\displaystyle\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[(\alpha\mu(n|S_{k_{n,t}^{*},t},\theta_{k_{n,t}^{*}})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))Q_{n}(t)\mathbb{1}(E_{t}^{1}\cap E_{t}^{2}\cap E_{n,t}^{3})]}\\ &{\displaystyle\leq\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[(\alpha\mu(n|S_{k_{n,t}^{*},t},\theta_{k_{n,t}^{*}})-\tilde{\mu}_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})}\\ &{\displaystyle}&{+\tilde{\mu}_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))Q_{n}(t)\mathbb{1}(E_{t}^{1}\cap E_{t}^{2}\cap E_{n,t}^{3})].}\end{array}
$$

$$
\widetilde{\Theta}_{t}=\left\{\{\theta_{k}^{(i)}\}_{i\in[M],k\in[K]}:\left|\operatorname*{max}_{i\in[M]}x_{n}^{\top}\theta_{k}^{(i)}-x_{n}^{\top}\hat{\theta}_{k,t}\right|\leq\gamma_{t}\|x_{n}\|_{V_{k,t}^{-1}};\,\forall n\in[N],\forall k\in[K]\right\}
$$

$$
\mathbb{P}\left(\sum_{k\in[K]}\sum_{n\in S_{k,t}^{\alpha}}\widetilde{\mu}_{t}^{T S}(n|S_{k,t})Q_{n}(t)>\sum_{k\in[K]}\sum_{n\in S_{k,t}^{*}}\alpha\mu(n|S_{k,t}^{*},\theta_{k})Q_{n}(t)|\mathcal{F}_{t-1},E_{t}^{1}\right)\geq1/4\sqrt{e\pi}
$$

$$
\begin{array}{r l}&{\mathbb{P}\left(\underset{i\in[M]}{\operatorname*{max}}\;x_{n}^{\top}\widetilde{\theta}_{k,t}^{(i)}>x_{n}^{\top}\theta_{k}|\mathcal{F}_{t-1},E_{t}^{1}\right)=1-\mathbb{P}\left(x_{n}^{\top}\theta_{k,t}^{(i)}\leq x_{n}^{\top}\theta_{k};\forall i\in[M]|\mathcal{F}_{t-1},E_{t}^{1}\right)}\\ &{\quad\quad\quad\quad\quad\quad\quad\quad=1-\mathbb{P}\left(Z_{i}\leq\frac{x_{n}^{\top}\theta_{k}-x_{n}^{\top}\hat{\theta}_{k,t}}{\beta_{t}\Vert x_{n}\Vert_{V_{k,t}^{-1}}};\forall i\in[M]|\mathcal{F}_{t-1},E_{t}^{1}\right)}\\ &{\quad\quad\quad\quad\quad\quad\quad\quad\quad\geq1-\mathbb{P}\left(Z\leq1\right)^{M},}\end{array}
$$

$$
\begin{array}{r l}&{\mathbb{S}^{\pi}(T)=\displaystyle\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[Q_{n}(t)(\alpha\mu(n|S_{k_{n,t}^{\ast},t},\theta_{k_{n,t}^{\ast}})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))]}\\ &{\le\displaystyle\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[Q_{n}(t)(\alpha\mu(n|S_{k_{n,t}^{\ast},t},\theta_{k_{n,t}^{\ast}})-\widetilde\mu_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})+\widetilde\mu_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))}\\ &{\ +\displaystyle\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[Q_{n}(t)(\alpha\mu(n|S_{k_{n,t}^{\ast},t},\theta_{k_{n,t}^{\ast}})-\widetilde\mu_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})+\widetilde\mu_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))}\\ &{\ +\displaystyle\sum_{t\in[T]}\sum_{n\in[N]}\mathbb{E}[Q_{n}(t)(\alpha\mu(n|S_{k_{n,t}^{\ast},t},\theta_{k_{n,t}^{\ast}})-\widetilde\mu_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})+\widetilde\mu_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))}\end{array}
$$

$$
\begin{array}{r l}&{\displaystyle\sum_{t\in[T]}\displaystyle\sum_{n\in[N]}\mathbb{E}[Q_{n}(t)(\alpha\mu(n|S_{k_{n,t}^{*},t},\theta_{k_{n,t}^{*}})-\widetilde{\mu}_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})}\\ &{\qquad\qquad\qquad\qquad+\widetilde{\mu}_{t}^{T S}(n|S_{k_{n,t},t}^{\alpha})-\mu(n|S_{k_{n,t},t}^{\alpha},\theta_{k_{n,t}}))\mathbb{1}(E_{t}^{1}\cap E_{t}^{2})]}\\ &{\leq C_{2}\displaystyle\sum_{t\in[T]}\displaystyle\sum_{k\in[K]}\mathbb{E}\left[\operatorname*{max}_{n\in S_{k,t}^{\alpha}}(\gamma_{t}+\beta_{t})\lVert x_{n}\rVert_{V_{k,t}^{-1}}Q_{n}(t)\right].}\end{array}
$$
