\begin{array}{c}{{V^{\pi}(h)=\mathbb{E}_{s|h}[V^{\pi}(h,s)],}}\\ {{Q^{\pi}(h,a)=\mathbb{E}_{s|h}[Q^{\pi}(h,s,a)].}}\end{array}
$$

$$
\begin{array}{r l}&{\mathbb{E}_{s|h}[V_{\mathrm{dual}}^{\pi}(h,s)]=\mathbb{E}_{s|h}\big[(1-\beta)V^{\pi}(h,s)+\beta V^{\pi}(h)\big]}\\ &{\qquad\qquad\qquad\qquad=(1-\beta)\mathbb{E}_{s|h}[V^{\pi}(h,s)]+\beta V^{\pi}(h)}\\ &{\qquad\qquad\qquad\qquad=V^{\pi}(h).}\end{array}
$$

$$
\begin{array}{r l}&{\mathrm{Var}_{s\mid h}[V_{\mathrm{dual}}^{\pi}(h,s)]=\mathrm{Var}_{s\mid h}\big[(1-\beta)V^{\pi}(h,s)+\beta V^{\pi}(h))\big]}\\ &{\qquad\qquad\qquad\qquad=(1-\beta)^{2}\mathrm{Var}_{s\mid h}[V^{\pi}(h,s)]}\\ &{\qquad\qquad\qquad\qquad\leq\mathrm{Var}_{s\mid h}[V^{\pi}(h,s)].}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{\mathrm{policy}}^{\mathrm{DCRL}}(\theta)=-\mathbb{E}\Big[\displaystyle\sum_{t=0}^{\infty}\big[(1-\beta)\delta_{\psi}(h_{t},s_{t},a_{t})\mathrm{log}\pi_{\theta}(a_{t}|h_{t})+\beta\delta_{\phi}(h_{t},a_{t})\mathrm{log}\pi_{\theta}(a_{t}|h_{t})\big]\Big],}\\ &{\qquad\qquad\qquad\mathcal{L}_{\mathrm{value}}^{\mathrm{DCRL}}(\phi,\psi)=\mathbb{E}\Big[\displaystyle\sum_{t=0}^{\infty}\delta_{\phi}(h_{t},a_{t})^{2}\Big]+\mathbb{E}\Big[\displaystyle\sum_{t=0}^{\infty}\delta_{\psi}(h_{t},s_{t},a_{t})^{2}\Big],}\end{array}
$$

$$
\begin{array}{c}{{V(h)=\alpha\log\sum_{a}\exp\left(Q(h,a)/\alpha\right),}}\\ {{\pi(a\mid h)=\exp\left(\left(Q(h,a)-V(h)\right)/\alpha\right),}}\\ {{Q(h,a)=V(h)+\alpha\log\pi(a\mid h).}}\end{array}
$$

$$
\begin{array}{l}{\hat{R}=R-\alpha\log\pi(a\mid h),}\\ {\Delta=R-Q(h,a)=\hat{R}-V(h).}\end{array}
$$
