$$
\begin{array}{r l}{\lefteqn{I o g(M_{P r b})=\sum_{i=1}^{k}w_{i}l o g(B_{P r_{i}})-\sum_{i=1}^{k}w_{i}l o g(B_{P r_{i}})}}\\ &{=\sum_{i=1}^{k}w_{i}(l o g(m(m_{P r_{i}}^{\prime},l_{P r_{i}}^{\prime}))+l o g(e^{R_{P r_{i}}}(\lambda))+l o g(\rho^{R_{r_{i}}}(\lambda)))}\\ &{=\sum_{i=1}^{k}w_{i}(l o g(m(m_{P r_{i}}^{\prime},l_{P r_{i}}^{\prime}))+l o g(e^{B_{P r_{i}}}(\lambda))+l o g(\rho^{B_{P r_{i}}}(\lambda)))}\\ &{=\sum_{i=1}^{k}w_{i}l o g(e^{R_{P r_{i}}}(\lambda))-\sum_{i=1}^{k}w_{i}l o g(e^{B_{P r_{i}}}(\lambda))}\\ &{+\sum_{i=1}^{k}w_{i}l o g(\rho^{B_{P r_{i}}}(\lambda))-\sum_{i=1}^{k}w_{i}l o g(\rho^{B_{P r_{i}}}(\lambda))}\end{array}
$$
