$$
\begin{array}{r l}&{\exists\mathrm{Error}\left(f\right)=\mathbb{E}_{x\sim\mathcal{D}}[\displaystyle\sum_{m=1}^{M}\|\sum_{i=1}^{M}D(\omega^{(i)}\cdot E^{(i)}(x^{(i)}))-x^{(m)}\|]}\\ &{\qquad\qquad\le\displaystyle\frac{1}{M}\sum_{m=1}^{M}\mathbb{E}\big[(2M-1)\|D(E^{(m)}(x^{(m)}))-x^{(m)}\|+(M-1)\displaystyle\sum_{i\neq m}^{M}\|D(E^{(i)}(x^{(i)}))-x^{(m)}\|}\\ &{\qquad\qquad+\displaystyle\sum_{m=1}^{M}C o v\big(\omega^{(m)},\|\underbrace{D\left(E^{(m)}\left(x^{(m)}\right)\right)}_{u n i\cdot s a t r e\ c o m p o n e n t}-x^{(m)}\|\big).}\end{array}
$$

$$
\begin{array}{r l r}{\lefteqn{\ell(I_{F},\boldsymbol{x})=\sum_{m=1}^{M}\|I_{F}-\boldsymbol{x}^{(m)}\|=\sum_{m=1}^{M}\left(\|\sum_{i=1}^{M}D(\boldsymbol{\omega}^{(i)}\cdot\boldsymbol{E}^{(i)}(\boldsymbol{x}^{(i)}))-\boldsymbol{x}^{(m)}\|\right)}}\\ &{}&{\leq\sum_{m=1}^{M}\left(\|\sum_{i=1}^{M}\boldsymbol{\omega}^{(i)}\cdot D(\boldsymbol{E}^{(i)}(\boldsymbol{x}^{(i)}))-\boldsymbol{x}^{(m)}\|\right)}\\ &{}&{=\displaystyle\sum_{m=1}^{M}\left(\sum_{i=1}^{M}\boldsymbol{\omega}^{(i)}\cdot\|D(\boldsymbol{E}^{(i)}(\boldsymbol{x}^{(i)}))-\boldsymbol{x}^{(m)}\|\right).}\end{array}
$$
