$$
\begin{array}{r l}&{I(A;C\mid X)=I(A;C,X)-I(A;X)}\\ &{\qquad\qquad=I(A;C)+I(A;X\mid C)-I(A;X)}\\ &{\qquad\quad=I(A;A+\lambda C\mid C)-I(A;A+\lambda C)}\\ &{\qquad\quad=I(A;A)-I(A;A+\lambda C)}\\ &{\qquad\quad=H(A)-H(A+\lambda C)+H(A+\lambda C\mid A)}\\ &{\qquad\quad=H(A)-H(A+\lambda C)+H(\lambda C).}\end{array}
$$

$$
\begin{array}{r c l}{{}}&{{}}&{{\displaystyle{H(A+\lambda^{\prime})=\sum_{t=1}^{N-1}\alpha_{t}\mathrm{\boldmath~\hat{\scriptstyle\delta}~}_{\mathrm{T}+1}}}}\\ {{}}&{{}}&{{\displaystyle{\sum_{t=1}^{N-1}\alpha_{t}\mathrm{\boldmath~\hat{\scriptstyle\delta}~}_{\mathrm{T}+1}}}}\\ {{}}&{{}}&{{\displaystyle{=(N_{A}N_{C}-\sum_{t=1}^{N-1}\alpha_{t})y_{\mathrm{B}+\lambda}\sum_{t=1}^{N-1}\alpha_{t}\mathrm{\boldmath~\hat{\scriptstyle\delta}~}_{\mathrm{T}+1}}}}\\ {{}}&{{}}&{{\displaystyle{=N_{A}N_{C}y_{\mathrm{B}+1}+\sum_{t=1}^{N-1}\alpha_{t}(y_{t}-y_{\mathrm{B}})}}}\\ {{}}&{{}}&{{\displaystyle{=N_{A}N_{C}y_{\mathrm{B}+1}+\sum_{t=1}^{N-1}\alpha_{t}(y_{t}-y_{\mathrm{B}})}}}\\ {{}}&{{}}&{{\displaystyle{=N_{A}N_{C}y_{\mathrm{B}+1}+(y_{\mathrm{B}+1}-y_{\mathrm{B}+1})\sum_{t=1}^{N-1}\alpha_{t}\mathrm{\boldmath~\hat{\scriptstyle\delta}~}_{\mathrm{T}+1}}}}\\ {{}}&{{}}&{{\displaystyle{\left(y_{\mathrm{B}+2}-y_{\mathrm{B}+1}-\sum_{t=1}^{N-2}\alpha_{t}\mathrm{\boldmath~\hat{\scriptstyle\delta}~}_{\mathrm{T}+1}+(y_{2}-y_{\mathrm{B}})(x_{1}+2x_{2})+(y_{1}-y_{2})x_{1}\right)}}}\\ {{}}&{{}}&{{\displaystyle{\geq N_{A}N_{C}y_{\mathrm{B}+1}+X_{A}(X_{4}-1)(y_{\mathrm{B}+1}-y_{\mathrm{B}+1})+}}}\\ {{}}&{{}}&{{\displaystyle{\left(N_{A}-1\right)(N_{A}-2)(y_{\mathrm{B}+2}-y_{\mathrm{B}+1})+\ldots++6(y_{2}-y_{\mathrm{B}})+2(y_{1}-y_{2})}}
$$

$$
\begin{array}{r l}&{H(A+C)=-2\displaystyle\sum_{i=1}^{N_{C}-1}(\frac{i}{N_{A}N_{C}}\ln\frac{i}{N_{A}N_{C}})-\left(N_{A}-N_{C}+1\right)\displaystyle\frac{1}{N_{A}}\ln\frac{1}{N_{A}}}\\ &{\phantom{\frac{(A+C)}{1}}\displaystyle\sum_{i=1}^{N_{C}-1}[\frac{i}{N_{A}N_{C}}(\ln N_{A}+\ln N_{C}-\ln i)]+\displaystyle\frac{N_{A}-N_{C}+1}{N_{A}}\ln N_{A}}\\ &{\phantom{\frac{(A+C)}{1}}=\displaystyle\frac{N_{C}-1}{N_{A}}(\ln N_{A}+\ln N_{C})-\displaystyle\frac{2}{N_{A}N_{C}}\sum_{i=1}^{N_{C}-1}(i\ln i)+\displaystyle\frac{N_{A}-N_{C}+1}{N_{A}}\ln N_{A}}\\ &{\phantom{\frac{(A+C)}{1}}=\ln N_{A}+\displaystyle\frac{N_{C}-1}{N_{A}}\ln N_{C}-\displaystyle\frac{2}{N_{A}N_{C}}\sum_{i=1}^{N_{C}-1}(i\ln i).}\end{array}
$$

$$
\begin{array}{r l}&{I(Z;C)-I(Z;A)\ge I(Z;C;A)-I(Z;A)}\\ &{\qquad\qquad\qquad=-\ I(Z;A|C)}\\ &{\qquad\qquad\qquad\ge-\ I(X;A|C),}\end{array}
