$$
\begin{array}{c}{\displaystyle\operatorname*{min}_{\boldsymbol{\pi}_{1}}\sum_{p_{i}\in\mathcal{M}\cup\boldsymbol{\mathcal{C}}}f^{a}\bigl(\pi_{1}(p_{i})\bigr)+\lambda_{1}\displaystyle\sum_{p_{i}\in\mathcal{M}}w(p_{i})\bigl\|\Delta\pi_{1}(p_{i})\bigr\|^{2},\mathrm{where}}\\ {\Delta\pi_{1}(p_{i})=\pi_{1}(p_{i})-\displaystyle\frac{1}{|\mathcal{N}(p_{i})|}\displaystyle\sum_{p_{j}\in\mathcal{N}(p_{i})}\pi_{1}(p_{j})}\end{array}
$$

$$
\begin{array}{l}{\displaystyle\alpha=\int_{0}^{+\infty}w(t)\,\mathrm{d}t=\int_{0}^{+\infty}-\frac{\mathrm{d}}{\mathrm{d}t}T(t)\,\mathrm{d}t}\\ {\displaystyle\qquad\qquad\qquad=T(0)-\operatorname*{lim}_{t\to+\infty}T(t)}\\ {\displaystyle\qquad\qquad=1-\operatorname*{lim}_{t\to+\infty}T(t)}\end{array}
$$

$$
\begin{array}{r l}&{\alpha=1-\underset{t\rightarrow\infty}{\operatorname*{lim}}T(t)}\\ &{\quad=1-\exp\left(-\int_{0}^{+\infty}\sigma(\mathbf{r}(t))\,\mathrm{d}t\right)}\\ &{\quad=1-\exp\left[-\int_{0}^{+\infty}\operatorname*{max}\left(\frac{-s e^{-s\int(\mathbf{r}(t))}}{1+e^{-s\int(\mathbf{r}(t))}}\,\mathrm{\cdot}\cos(\theta),0\right)\,\mathrm{d}t\right]}\\ &{\quad=1-\exp\left(-\int_{0}^{t}\frac{-s e^{-s\int(\mathbf{r}(t))}}{1+e^{-s\int(\mathbf{r}(t))}}\,\mathrm{\cdot}\cos(\theta)\,\mathrm{d}t\right)}\\ &{\quad=1-\exp\left(-\int_{0}^{\infty}\frac{1}{s(t)\cos(\theta)+1}\frac{-e^{-s\int(\mathbf{r}(t))}}{1+e^{-s\int(\mathbf{r}(t))}}\,\mathrm{\d}(s f(\mathbf{r}(t)))\right)}\\ &{\quad=1-\frac{e^{s\cdot m}+e^{-s\cdot\theta_{0}\left(\cos(\theta)\right)}}{1+e^{-s\cdot m}}}\\ &{\quad=\frac{1-e^{-s\cdot\theta_{0}\left(\cos(\theta)\right)}}{1+e^{-s\cdot\theta_{0}\left(\cos(\theta)\right)}},}\end{array}
$$

$$
\begin{array}{r l}&{\alpha=1-\underset{t\rightarrow+\infty}{\operatorname*{lim}}T(t)}\\ &{\quad=1-\exp\left(-\int_{0}^{+\infty}\sigma(\mathbf{r}(t))\,\mathrm{d}t\right)}\\ &{\quad=1-\exp\left[-\int_{0}^{+\infty}\operatorname*{max}\left(\frac{-s e^{-s\int(\mathbf{r}(t))}}{1+e^{-s\int(\mathbf{r}(t))}}\,\mathrm{.~cos}(\theta),0\right)\,\mathrm{d}t\right]}\\ &{\quad=1-\exp\left(-\int_{0}^{t_{0}+(-m)/(\cos(\theta))}\frac{-s e^{-s\int(\mathbf{r}(t))}}{1+e^{-s\int(\mathbf{r}(t))}}\,\mathrm{\cdot}\cos(\theta)\,\mathrm{d}t\right)}\\ &{\quad=1-\exp\left(-\int_{0}^{\infty}\frac{1}{1+e^{-s\int(\mathbf{r}(t))}}\,\mathrm{\frac{~}{~d}s\int(\mathbf{r}(t))}\,\mathrm{\cdot}\cos(\theta)\,\mathrm{d}t\right)}\\ &{\quad=1-\exp\left(-\int_{s\in\mathbb{N}(\cos(\theta))}^{\infty}\frac{-e^{-s\int(\mathbf{r}(t))}}{1+e^{-s\int(\mathbf{r}(t))}}\,\mathrm{d}(s f(\mathbf{r}(t)))\right)}\\ &{\quad=1-\frac{1+e^{-s\cdot\mathbf{r}(\cos(\theta))}}{1+e^{-s\cdot m}},}\end{array}
$$

$$
\begin{array}{r l}&{w^{\prime}(t)=[T(t)\sigma({\bf r}(t))]^{\prime}}\\ &{\quad\quad=T^{\prime}(t)\sigma({\bf r}(t))+T(t)\sigma^{\prime}({\bf r}(t))}\\ &{\quad\quad=(-T(t)\sigma({\bf r}(t)))\sigma({\bf r}(t))+T(t)\sigma^{\prime}({\bf r}(t))}\\ &{\quad\quad=-T(t)\sigma^{2}({\bf r}(t))+T(t)\sigma^{\prime}({\bf r}(t))}\\ &{\quad\quad=T(t)\left[\sigma^{\prime}({\bf r}(t))-\sigma^{2}({\bf r}(t))\right]}\end{array}
$$

$$
\begin{array}{r l}&{\sigma^{\prime}(\mathbf{r}(t^{*}))=\left[-s\left(1-\cfrac{1}{1+e^{-s f(\mathbf{r}(t^{*}))}}\right)\cos(\theta)\right]^{\prime}}\\ &{\qquad\qquad=\cfrac{s^{2}\cos^{2}(\theta)e^{-s f(\mathbf{r}(t^{*}))}}{\left(1+e^{-s f(\mathbf{r}(t^{*}))}\right)^{2}},}\end{array}
$$

$$
\begin{array}{r l}&{\qquad m>0}\\ &{\Longrightarrow\sigma^{2}({\bf r}(t))=\sigma^{\prime}({\bf r}(t))\cdot e^{-s f({\bf r}(t^{*}))}<\sigma^{\prime}({\bf r}(t))\cdot e^{-s\cdot0}=\sigma^{\prime}({\bf r}(t))}\\ &{\Longrightarrow w^{\prime}(t)>0.}\end{array}
$$
