$$
\begin{array}{r}{\mathcal{L}(\mathbf{G}^{1},\ldots,\mathbf{G}^{L})=\log\mathrm{P}\left(\mathbf{Y}|\mathbf{G}^{L}\right)-\sum_{\ell=1}^{L}\!\nu_{\ell}\,\mathrm{D}_{\mathrm{KL}}\left(\!N\left(\mathbf{0},\mathbf{G}^{\ell}\right)\left|\left|\mathcal{N}\left(\mathbf{0},\mathbf{K}(\mathbf{G}^{\ell-1})\right)\right).}\end{array}
$$

$$
\mathrm{D}_{\mathrm{KL}}\left(\mathcal{N}\left(\mathbf{0},\mathbf{G}\right)\Vert\mathcal{N}\left(\mathbf{0},\mathbf{K}\right)\right)=\mathrm{Tr}(\mathbf{K}^{-1}\mathbf{G})-\log\operatorname*{det}(\mathbf{K}^{-1}\mathbf{G})+\mathrm{const.}
$$

$$
\begin{array}{r l}&{-\mathrm{logdet}(\mathbf{K}^{-1}\mathbf{G})=\mathrm{logdet}(\mathbf{G}^{-1}\mathbf{K})}\\ &{\qquad\qquad\qquad=\displaystyle\sum_{i}\log\lambda_{i}}\\ &{\qquad\qquad\approx\displaystyle\sum_{i}(\lambda_{i}-1)-\frac{1}{2}(\lambda_{i}-1)^{2}}\\ &{\qquad\qquad\qquad=\mathrm{Tr}(\mathbf{G}^{-1}\mathbf{K}-\mathbf{I})-\frac{1}{2}\mathrm{Tr}\left[(\mathbf{G}^{-1}\mathbf{K}-\mathbf{I})^{2}\right].}\end{array}
$$

$$
\begin{array}{r l}{\lefteqn{\mathrm{Tr}(\mathbf{K}^{-1}\mathbf{G})=\sum_{i}\frac{1}{\lambda_{i}}}\quad}&{}\\ &{\approx\displaystyle\sum_{i}1-(\lambda_{i}-1)+(\lambda_{i}-1)^{2}}\\ &{=-\mathrm{Tr}(\mathbf{G}^{-1}\mathbf{K}-\mathbf{I})+\mathrm{Tr}\left[(\mathbf{G}^{-1}\mathbf{K}-\mathbf{I})^{2}\right]+\mathrm{const.}}\end{array}
$$

$$
\begin{array}{r}{\mathrm{D}_{\mathrm{KL}}\left(\boldsymbol{N}\left(\mathbf{0},\mathbf{G}\right)\right\Vert\boldsymbol{N}\left(\mathbf{0},\mathbf{K}\right)\right)\approx\frac{1}{2}\mathrm{Tr}\left[\left(\mathbf{G}^{-1}\mathbf{K}-\mathbf{I}\right)^{2}\right]+\mathrm{const}=\frac{1}{2}|\mathbf{G}^{-1}\mathbf{K}-\mathbf{I}|_{\mathcal{F}}^{2}+\mathrm{const},}\end{array}
$$

$$
\begin{array}{l}{{\displaystyle\mathbf{F}^{0}=\mathbf{X},}}\\ {{\displaystyle\mathrm{P}(\mathbf{F}^{\ell}\mid\mathbf{F}^{\ell-1})=\prod_{\lambda=1}^{N_{\ell}}\mathcal{N}(\mathbf{f}_{\lambda}^{\ell};\mathbf{0},\mathbf{K}_{\mathrm{features}}(\mathbf{F}^{\ell-1})),}}\\ {{\displaystyle\mathrm{P}(\mathbf{y}\mid\mathbf{F}^{L+1})=\prod_{i=1}^{P}\mathrm{Categorical}(y_{i};\mathrm{softmax}((\mathbf{F}^{L+1})_{i,:})).}}\end{array}
$$

$$
\begin{array}{l}{\displaystyle\mathrm{P}(\mathbf{F}^{\ell}\mid\mathbf{X},\mathbf{Y})\approx\mathrm{Q}(\mathbf{F}^{\ell})=\displaystyle\prod_{\lambda=1}^{N_{\ell}}\mathrm{Q}(\mathbf{f}_{\lambda}^{\ell})=\displaystyle\prod_{\lambda=1}^{N_{\ell}}\mathcal{N}(\mathbf{f}_{\lambda}^{\ell};\mathbf{0},\mathbf{G}^{\ell}),}\\ {\displaystyle\mathrm{P}(\mathbf{F}^{L+1}\mid\mathbf{X},\mathbf{Y})\approx\mathrm{Q}(\mathbf{F}^{L+1})=\displaystyle\prod_{\lambda=1}^{N_{\ell}}\mathrm{Q}(\mathbf{f}_{\lambda}^{L+1})=\displaystyle\prod_{\lambda=1}^{N_{L+1}}\mathcal{N}(\mathbf{f}_{\lambda}^{L+1};{\boldsymbol{\mu}}_{\lambda},\mathbf{\Sigma}).}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{\mathrm{ELBO}}(\mathbf{G}_{1},\dots,\mathbf{G}_{L},\mu_{1},\dots,\mu_{N_{L+1}},\Sigma)=}\\ &{\qquad\mathbb{E}_{\mathbf{Q}(\mathbf{F}^{L+1})}\left[\log\mathrm{P}(\mathbf{y}\mid\mathbf{F}^{L+1})\right]-\displaystyle\sum_{\lambda=1}^{N_{L+1}}\mathrm{D}_{\mathrm{KL}}(\mathbf{Q}(\mathbf{f}_{\lambda}^{L+1})\mid|\,\mathrm{P}(\mathbf{f}_{\lambda}^{L+1}\mid\mathbf{F}^{L}))}\\ &{-\displaystyle\sum_{\ell=1}^{L}\beta N_{\ell}\mathrm{D}_{\mathrm{KL}}(\mathbf{Q}(\mathbf{f}^{\ell})\mid|\,\mathrm{P}(\mathbf{f}^{\ell}\mid\mathbf{F}^{\ell-1})).}\end{array}
$$

$$
\begin{array}{r}{{\bf K}_{\mathrm{features}}({\bf F}^{\ell})={\bf K}(\hat{\bf G}^{\ell}),}\\ {\mathrm{where}\;\hat{\bf G}^{\ell}=\frac{1}{N_{\ell}}{\bf F}^{\ell}({\bf F}^{\ell})^{T}.}\end{array}
$$

$$
\begin{array}{r}{\mathcal{L}_{\mathrm{ELBO}}\rightarrow\mathcal{L}:=\mathbb{E}_{\mathrm{Q}(\mathbf{F}^{L+1})}\left[\log\mathrm{P}(\mathbf{y}\mid\mathbf{F}^{L+1})\right]-\displaystyle\sum_{\lambda=1}^{N_{L+1}}\mathrm{D}_{\mathrm{KL}}(\mathcal{N}(\boldsymbol{\mu}_{\lambda},\mathbf{Z})\mid\mid\mathcal{N}(\mathbf{0},\mathbf{K}(\mathbf{G}^{L}))}\\ {-\displaystyle\sum_{\ell=1}^{L}\nu_{\ell}\mathrm{D}_{\mathrm{KL}}(\mathcal{N}(\mathbf{0},\mathbf{G}^{\ell})\mid\mid\mathcal{N}(\mathbf{0},\mathbf{K}(\mathbf{G}^{\ell-1}))),}\end{array}
$$

$$
\begin{array}{r l r}{\lefteqn{\mathbf{F}^{0}=\mathbf{X},}}\\ &{}&{\mathrm{P}(\mathbf{H}^{\ell}\mid\mathbf{F}^{\ell})=\displaystyle\prod_{\lambda=1}^{M_{\ell}}\mathcal{N}(\mathbf{h}_{\lambda}^{\ell};\mathbf{0},\mathbf{K}_{\mathrm{feaures}}(\mathbf{F}^{\ell})),}\\ &{}&{W_{d\mu\lambda}^{\ell}\sim_{\mathrm{iid}}\mathcal{N}(0,(|\mathcal{D}|M_{\ell-1})^{-1}),}\\ &{}&{F_{i r,\lambda}^{\ell}=\displaystyle\sum_{d\in\mathcal{D}}\sum_{\mu=1}^{M_{\ell-1}}H_{i(r+d),\mu}^{\ell-1}W_{d\mu\lambda}^{\ell},}\end{array}
$$

$$
\begin{array}{l}{{\displaystyle{\bf F}_{\mathrm{flat}}^{L}=\mathrm{SpatialPool}({\bf F}^{L})}\ ~}\\ {{\displaystyle\mathrm{P}({\bf F}^{L+1}\mid{\bf F}^{L})=\prod_{\lambda=1}^{N_{L+1}}\mathcal{N}({\bf f}_{\lambda}^{L+1};{\bf0},{\bf K}_{\mathrm{feaures}}({\bf F}_{\mathrm{flat}}^{L})),}}\\ {{\displaystyle\mathrm{P}({\bf y}\mid{\bf F}^{L+1})=\prod_{i=1}^{P}\mathrm{Categorical}(y_{i};\mathrm{softmax}(({\bf F}^{L+1})_{i,:})).}}\end{array}
$$

$$
\begin{array}{r l}&{\mathbb{E}[F_{i r,\mu}^{\ell}F_{j s,\mu^{\prime}}^{\ell}\mid\mathbf{H}^{\ell-1}]=\mathbb{E}\Bigg[\sum_{d\mu}H_{i(r+d)\mu}^{\ell-1}W_{d\mu,\lambda}^{\ell}\sum_{\substack{d^{\prime}\mu^{\prime}}}H_{j(s+d^{\prime})\mu^{\prime}}^{\ell-1}W_{d\mu^{\prime},\lambda}^{\ell}\Bigg]}\\ &{\qquad\qquad\qquad=\displaystyle\sum_{d^{\prime}\mu\mu^{\prime}}H_{i(r+d)\mu}^{\ell-1}H_{j(s+d^{\prime})\mu^{\prime}}^{\ell-1}\mathbb{E}[W_{d\mu,\lambda}^{\ell}W_{d^{\prime}\mu^{\prime},\lambda}^{\ell}]}\\ &{\qquad\qquad\qquad=\displaystyle\frac{1}{D M_{\ell}}\sum_{d\in\mathcal{D}}\sum_{\mu=1}^{M_{\ell}}H_{i,(r+d)\mu}^{\ell-1}H_{j,(s+d)\mu^{\prime}}^{\ell-1}}\\ &{\qquad\qquad\qquad=\displaystyle\frac{1}{D}\sum_{d\in\mathcal{D}}\hat{\Omega}_{i(r+d),j(s+d)}^{\ell-1},}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}:=\mathbb{E}_{\mathrm{Q}(\mathbf{F}^{L+1})}\left[\log\mathrm{P}(\mathbf{y}\mid\mathbf{F}^{L+1})\right]-\displaystyle\sum_{\lambda=1}^{N_{L+1}}\mathrm{D}_{\mathrm{KL}}(\mathcal{N}(\boldsymbol{\mu}_{\lambda},\mathbf{Z})\mid|\mathcal{N}(\mathbf{0},\mathbf{K}(\mathrm{SpatialPool}(\mathbf{G}^{L}))))}\\ &{\quad\quad\quad\quad\quad\quad-\displaystyle\sum_{\ell=1}^{L}\nu_{\ell}\mathrm{D}_{\mathrm{KL}}(\mathcal{N}(\mathbf{0},\mathbf{G}^{\ell})\mid|\mathcal{N}(\mathbf{0},\mathbf{F}(\mathbf{K}(\mathbf{G}^{\ell-1})))),\quad\quad}\end{array}
$$

$$
\mathbf{F}^{\ell}=\left({\mathbf{F}}_{\mathrm{i}}^{\ell}\right)\in\mathbb{R}^{(P_{\mathrm{i}}^{\ell}+P_{\mathrm{t}})\times N_{\ell}}
$$

$$
\begin{array}{r l}&{\mathbb{E}\big[(F_{\mathrm{i}}^{\ell})_{i,\lambda}(F_{\mathrm{i}}^{\ell})_{j,\lambda}\mid\mathbf{H}^{\ell-1}\big]=\displaystyle\frac{1}{D}\sum_{d\in\mathcal{D}}\sum_{i^{\prime}}\sum_{j^{\prime}}C_{d i,i^{\prime}}^{\ell}C_{d j,j^{\prime}}^{\ell}(\hat{\Omega}_{\mathrm{ii}}^{\ell-1})_{i^{\prime}j^{\prime}}:=(\Gamma_{\mathrm{ii}}^{\ell}(\hat{\Omega}^{\ell-1}))_{i,j},}\\ &{\mathbb{E}\big[(F_{\mathrm{i}}^{\ell})_{i,\lambda}(F_{\mathrm{t}}^{\ell})_{j\circ,\lambda}\mid\mathbf{H}^{\ell-1}\big]=\displaystyle\frac{1}{D}\sum_{d\in\mathcal{D}}\sum_{i^{\prime}}C_{d i,i^{\prime}}^{\ell}(\hat{\Omega}_{\mathrm{it}}^{\ell-1})_{i^{\prime},j(s+d)}:=(\Gamma_{\mathrm{it}}^{\ell}(\hat{\Omega}^{\ell-1}))_{i,j s}}\\ &{\mathbb{E}\big[(F_{\mathrm{t}}^{\ell})_{i s,\lambda}(F_{\mathrm{t}}^{\ell})_{j\circ,\lambda}\mid\mathbf{H}^{\ell-1}\big]=\displaystyle\frac{1}{D}\sum_{d\in\mathcal{D}}(\hat{\Omega}_{\mathrm{tt}}^{\ell-1})_{i(s+d),j(v+d)}:=\big(\Gamma_{\mathrm{ti}}^{\ell}(\hat{\Omega}^{\ell-1})\big)_{i s,j v}.}\end{array}
$$

$$
\begin{array}{r l r}{\lefteqn{\mathrm{Q}(\mathbf{F}_{\mathrm{i}}^{\ell})=\prod_{\lambda=1}^{N_{\ell}}\mathrm{Q}(\mathbf{f}_{\mathrm{i};\lambda}^{\ell})=\prod_{\lambda=1}^{N_{\ell}}\mathcal{N}(\mathbf{f}_{\mathrm{i};\lambda}^{\ell}\mathbf{0},\mathbf{G}_{\mathrm{ii}}^{\ell}),}}\\ &{}&{\mathrm{Q}(\mathbf{F}_{\mathrm{i}}^{L+1})=\displaystyle\prod_{\lambda=1}^{N_{\ell}}\mathrm{Q}(\mathbf{f}_{\mathrm{i};\lambda}^{L+1})=\displaystyle\prod_{\lambda=1}^{N_{L+1}}\mathcal{N}(\mathbf{f}_{\mathrm{i};\lambda}^{L+1};{\boldsymbol{\mu}}_{\lambda},\Sigma).}\end{array}
$$

$$
\begin{array}{r l}&{\mathcal{L}_{\mathrm{inducing}}:=\mathbb{E}_{\mathrm{Q}({\mathbf{F}}^{L+1})}\big[\log\mathrm{P}({\mathbf{y}}\mid{\mathbf{F}}_{\mathrm{t}}^{L+1})\big]}\\ &{\quad\quad\quad\quad-\displaystyle\sum_{\lambda=1}^{N_{L+1}}\mathrm{D}_{\mathrm{KL}}(\mathcal{N}({\boldsymbol\mu}_{\lambda},\Sigma)\mid\mid\mathcal{N}(\mathbf{0},{\mathbf{K}}(\mathrm{SpatialPool}({\mathbf{G}}_{\mathrm{ii}}^{L})))}\\ &{\quad\quad\quad\quad-\displaystyle\sum_{\ell=1}^{L}\nu_{\ell}\mathrm{D}_{\mathrm{KL}}(\mathcal{N}(\mathbf{0},{\mathbf{G}}_{\mathrm{ii}}^{\ell})\mid\mid\mathcal{N}(\mathbf{0},{\mathbf{T}}^{\ell}({\mathbf{K}}({\mathbf{G}}_{\mathrm{ii}}^{\ell-1})))),}\end{array}
$$

$$
\begin{array}{r l}&{\mathbf{G}_{\mathrm{ti}}^{\ell}=\underset{N_{\ell}\rightarrow\infty}{\operatorname*{lim}}\,\frac{1}{N_{\ell}}\mathbf{F}_{\mathrm{t}}^{\ell}(\mathbf{F}_{\mathrm{i}}^{\ell})^{T}=\mathbf{\Gamma}_{\mathrm{ti}}^{\ell}(\mathbf{T}_{\mathrm{ii}}^{\ell})^{-1}\mathbf{G}_{\mathrm{ii}}^{\ell}}\\ &{\mathbf{G}_{\mathrm{tt}}^{\ell}=\underset{N_{\ell}\rightarrow\infty}{\operatorname*{lim}}\,\frac{1}{N_{\ell}}\mathbf{F}_{\mathrm{t}}^{\ell}(\mathbf{F}_{\mathrm{t}}^{\ell})^{T}=\mathbf{\Gamma}_{\mathrm{ti}}^{\ell}(\mathbf{T}_{\mathrm{ii}}^{\ell})^{-1}\mathbf{G}_{\mathrm{ii}}^{\ell}(\mathbf{T}_{\mathrm{ii}}^{\ell})^{-1}\mathbf{I}_{\mathrm{it}}^{\ell}+\mathbf{\Gamma}_{\mathrm{tt}\cdot\mathrm{i}}.}\end{array}
