$$
\begin{array}{r l}&{\mathbf{Q}_{t}=\mathbf{W}^{Q}o_{t},\quad\mathbf{K}_{t}=\mathbf{W}^{K}o_{t},\quad\mathbf{V}_{t}=\mathbf{W}^{V}o_{t}\quad\mathrm{~for~all~}t,}\\ &{\alpha_{1}\ldots\alpha_{T}=\mathrm{softmax}\bigl([\mathbf{Q}_{t}^{\top}\mathbf{K}_{1}\ldots\mathbf{Q}_{t}^{\top}\mathbf{K}_{T}]/\sqrt{D}\bigr)\quad\quad y_{t}=\displaystyle\sum_{s=1}^{t}m_{s,t}\alpha_{s}\mathbf{V}_{s}}\\ &{\mathrm{where~}o_{t}\in\mathbb{R}^{D\times1},\quad\;\;\mathbf{W}\in\mathbb{R}^{N\times D}\quad\;\mathbf{Q}_{t},\mathbf{K}_{t},\mathbf{V}_{t}\in\mathbb{R}^{N\times1}\quad\;\;m_{s,t}=\mathbf{1}(s\leq t)}\end{array}
$$

$$
\begin{array}{r}{\pmb{h}_{t}=\mathbf{A}_{t}\pmb{h}_{t-1}+\mathbf{B}_{t}\pmb{x}_{t},\qquad\pmb{y}_{t}=\mathbf{C}_{t}\pmb{h}_{t}}\end{array}
$$

$$
\begin{array}{c}{\displaystyle h_{t}=m_{t-1,t}h_{t-1}+\mathbf{K}_{t}\mathbf{V}_{t}\quad y_{t}=\frac{1}{\sqrt{D}}\mathbf{Q}_{t}h_{t}}\\ {\downarrow}\\ {h_{t}=\mathbf{A}_{t}h_{t-1}+\mathbf{B}_{t}x_{t},\quad y_{t}=\mathbf{C}_{t}h_{t}}\\ {\mathbf{A}_{t}=m_{t-1,t},\;\mathbf{B}_{t}=\mathbf{W}^{K}o_{t},\;\mathbf{C}_{t}=\mathbf{W}^{Q}o_{t},\;x_{t}=\mathbf{W}^{V}o_{t}}\end{array}
$$

$$
\begin{array}{r}{\pmb{h}^{\prime}(k)=\mathbf{A}\pmb{h}(k)+\mathbf{B}(k)\pmb{x}(k)\ \ \ \ \pmb{y}(k)=\mathbf{C}(k)\pmb{h}(k)_{\mathrm{g}}^{7};}\end{array}
