$$
\begin{array}{r l}{p_{i}(\mathbf{q},\mathbf{b})=\displaystyle\int_{0}^{b_{i}}z\cdot\frac{d}{d z}\left(\frac{q_{i}z}{w_{-i}+q_{i}z}\right)d z}\\ {=\displaystyle\int_{0}^{b_{i}}z\cdot\left(\frac{w_{-i}/q_{i}}{(w_{-i}/q_{i}+z)^{2}}\right)d z}\\ {=\displaystyle\int_{0}^{b_{i}}\frac{w_{-i}z/q_{i}}{(w_{-i}/q_{i}+z)^{2}}d z}\\ {=\displaystyle\frac{w_{-i}}{q_{i}}\left(\frac{w_{-i}/q_{i}}{z+w_{-i}/q_{i}}+\ln\left(z+w_{-i}/q_{i}\right)\right)\Big\vert_{0}^{b_{i}}}\\ {=\displaystyle\frac{w_{-i}}{q_{i}}\Big(\frac{w_{-i}}{w_{-i}+q_{i}b_{i}}+\ln(b_{i}+w_{-i}/q_{i})-1-\ln(w_{-i}/q_{i})\Big)}\\ {=\displaystyle\frac{w_{-i}}{q_{i}}\Big(\ln(\frac{q_{i}h_{i}+w_{-i}}{w_{-i}})-\frac{q_{i}b_{i}}{w_{-i}+q_{i}b_{i}}\Big)}\\ {>0}\end{array}
$$

$$
\begin{array}{r l}&{(\hat{q}_{A}^{(t)})_{A\in\mathcal A_{k}}=\underset{x\in\Delta_{\left(\frac{n}{k}\right)}}{\operatorname{argmax}}\,\sum_{i\in\mathcal A_{k}}\hat{q}_{A}^{(t)}\log x_{A}}\\ &{\qquad\qquad=\underset{x\in\Delta_{\left(\frac{n}{k}\right)}}{\operatorname{argmax}}\,\sum_{i\in\mathcal A_{k}}\left(\underset{i\in A}{\sum_{i}}q_{A,i}^{(t)}v_{i}\right)\log x_{A}}\\ &{\qquad=\underset{x\in\Delta_{\left(\frac{n}{k}\right)}}{\operatorname{argmax}}\,\underset{i\in[n]}{\operatorname{suc}}\,\underset{u\in A_{k}\cdot i\in A}{\sum_{i}}q_{A,i}^{(t)}\log x_{A}\Bigg)\,.}\end{array}
