$$
\begin{array}{r c l}{\mathbb{P}(\underset{i\in S}{\operatorname*{min}}B_{i}\geq u)}&{=}&{\displaystyle\prod_{i\in S}(1-F(u-b_{i}))}\\ &{=}&{\displaystyle\sum_{T\subseteq S}(-1)^{|T|}\prod_{j\in T}F(u-b_{j})}\\ &{=}&{\displaystyle\sum_{T\subseteq S}(-1)^{|T|}\exp\left(-\sum_{j\in T}e^{b_{j}-u}\right)}\\ {\mathbb{P}(\underset{i\in S}{\operatorname*{min}}B_{i}\leq u)}&{=}&{\displaystyle1-\sum_{T\subseteq S}(-1)^{|T|}\exp\left(-\sum_{j\in T}e^{b_{j}-u}\right)}\end{array}
$$

$$
\begin{array}{r c l}{{\mathbb{P}(\underset{i\in\bar{S}}{\operatorname*{max}}B_{i}\leq u)}}&{{=}}&{{\displaystyle\prod_{i\in\bar{S}}F(u-b_{i})}}\\ {{}}&{{}}&{{}}\\ {{}}&{{=}}&{{\displaystyle\exp\left(-\sum_{i\in\bar{S}}e^{b_{i}-u}\right)}}\end{array}
$$

$$
\begin{array}{r c l}{\displaystyle^{\mathrm{v}}(S\operatorname*{mins})}&{=}&{\displaystyle\int_{-\infty}^{\infty}\mathbb{P}(\operatorname*{max}B_{i}\leq u)\cdot\mathbb{P}(\operatorname*{min}_{i\in S}B_{i}=u)\,d u}\\ &{=}&{\displaystyle\int_{-\infty}^{\infty}\exp\left(-\sum_{i\in S}e^{b_{i}-u}\right)\cdot\sum_{T\subseteq S}(-1)^{|T|+1}\exp\left(-\sum_{j\in T}e^{b_{j}-u}\right)\cdot\left(\sum_{j\in T}e^{b_{j}-u}\right)\,d u}\\ &{=}&{\displaystyle\sum_{T\subseteq S}(-1)^{|T|+1}\int_{-\infty}^{\infty}\exp\left(-\sum_{i\in S\cup T}e^{b_{i}-u}\right)\cdot\left(\sum_{j\in T}e^{b_{j}-u}\right)\,d u}\\ &{=}&{\displaystyle\sum_{T\subseteq S}(-1)^{|T|+1}\left(\sum_{j\in T}e^{b_{j}}\right)\int_{-\infty}^{\infty}\exp\left(-e^{-u}\sum_{i\in S\cup T}e^{b_{i}}\right)\cdot e^{-u}\,d u}\end{array}
$$

$$
{\begin{array}{r l}{\operatorname{\mathbb{P}}(S\operatorname*{sins})}&{=\quad\sum_{\tau\leq s}(-1)^{|T|+1}\left(\sum_{\ell\in T}\epsilon_{s}^{b}\right)\int_{\infty}^{0}\exp\left(-t\leq\sum_{\ell\leq s,T}\epsilon_{s}^{b}\right)\,(-d t)}\\ &{=\quad\sum_{\tau\leq s}(-1)^{|T|+1}\left(\sum_{\ell\in T}\epsilon_{s}^{b}\right)\int_{0}^{\infty}\exp\left(-t\sum_{\ell\leq s,T}\epsilon_{s}^{b}\right)\,d t}\\ &{=\quad\sum_{\tau\leq s}(-1)^{|T|+1}\left(\sum_{\ell\in T}\epsilon_{s}^{b}\right)\left({\frac{\exp\left(-t\sum_{\ell\in S,T}\epsilon_{s}^{b}\right)}{-\sum_{\ell\in S,T}\epsilon_{s}^{b}}}\right)_{\tau=0}^{|s|}}\\ &{=\quad\sum_{\tau\leq s}(-1)^{|T|+1}\left(\sum_{\ell\in T}\epsilon_{s}^{b}\right)\left(0-{\frac{1}{-\sum_{\ell\in S,T}\epsilon_{s}^{b}}}\right)}\\ &{=\quad\sum_{\tau\leq s}(-1)^{|T|+1}{\frac{\sum_{\ell\in T}\epsilon_{s}^{b}}{\sum_{\ell\in S,T}\epsilon_{s}^{b}}}}\end{array}}
$$
