$$
\begin{array}{r}{\mathcal{L}_{m}\big(\mu_{c},\mu_{l},\mu_{f};\mathcal{D}\big)=\mathcal{L}\big(\mu_{l}\odot\theta_{l},\mu_{c}\odot\theta_{c},\mu_{f}\odot\theta_{f};\mathcal{D}\big).}\end{array}
$$

$$
\begin{array}{r}{\hat{\Phi}_{\theta_{c}^{i}}=|\mathcal{L}_{m}(;\mathcal{D})-\mathcal{L}_{m}(\mu_{c}^{i}=0;\mathcal{D})|,}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{c}}=|\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})-\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{1})+...+\mathcal{L}_{m}(;\mathcal{D}_{B-1})-\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})|}\\ &{\quad\quad=|\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})-\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})|.}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{c,0}^{i}}=\big|\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})+\mu_{c}^{i}\odot\theta_{c,0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})}{\partial\theta_{c,0}^{i}}}\\ &{\qquad\qquad\qquad\qquad-\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})-\mu_{c}^{i}\odot\theta_{c,0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,0}^{i}}\big|.}\end{array}
$$

$$
\begin{array}{r l}&{S(\theta_{l}^{i})=\alpha\cdot\cfrac{\hat{\Phi}_{\theta_{l}^{i}}}{\sum_{j=0}^{N_{l}}\hat{\Phi}_{\theta_{l}^{j}}}-\beta\cdot\cfrac{\tilde{\Phi}_{\theta_{l}^{i}}}{\sum_{j=0}^{N_{l}}\tilde{\Phi}_{\theta_{l}^{j}}},}\\ &{S(\theta_{f}^{i})=\alpha\cdot\cfrac{\hat{\Phi}_{\theta_{f}^{i}}}{\sum_{j=0}^{N_{f}}\hat{\Phi}_{\theta_{f}^{j}}}-\frac{\beta}{2}\cdot\cfrac{\tilde{\Phi}_{\theta_{f}^{i}}(\mu_{l}=0)}{\sum_{j=0}^{N_{f}}\tilde{\Phi}_{\theta_{f}^{j}}(\mu_{l}=0)}-\frac{\beta}{2}\cdot\cfrac{\tilde{\Phi}_{\theta_{f}^{i}}(\mu_{c}=0)}{\sum_{j=0}^{N_{f}}\tilde{\Phi}_{\theta_{f}^{j}}(\mu_{c}=0)},}\end{array}
$$

$$
\begin{array}{r l}&{\displaystyle\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,0}^{i}}\cdot\theta_{c,0}^{i}=\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,B}^{i}}\cdot\frac{\partial\theta_{c,B}^{i}}{\partial\theta_{c,0}^{i}}\cdot\theta_{c,0}^{i}}\\ &{\quad\quad\quad\quad\quad\quad\quad=\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,B}^{i}}\cdot\frac{\partial\theta_{c,B}^{i}}{\partial\theta_{c,B-1}^{i}}\cdot\ldots\cdot\frac{\partial\theta_{c,2}^{i}}{\partial\theta_{c,1}^{i}}\cdot\frac{\partial\theta_{c,1}^{i}}{\partial\theta_{c,0}^{i}}\cdot\theta_{c,0}^{i}}\\ &{\quad\quad\quad\quad\quad\quad=\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,B}^{i}}\cdot\left[\displaystyle\prod_{j=1}^{B}\frac{\partial\theta_{c,j}^{i}}{\partial\theta_{c,j-1}^{i}}\right]\cdot\theta_{c,0}^{i}.}\end{array}
$$

$$
\begin{array}{r l}&{\displaystyle\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,0}^{i}}\cdot\theta_{c,0}^{i}=\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,B}^{i}}\cdot\left[\prod_{j=1}^{B}\frac{\partial\theta_{c,j}^{i}}{\partial\theta_{c,j-1}^{i}}\right]\cdot\theta_{c,0}^{i}}\\ &{\displaystyle\qquad\qquad\qquad\qquad=\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,B}^{i}}\cdot\left[\prod_{j=1}^{B}\frac{\partial\theta_{c,j-1}^{i}-\epsilon\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{j-1})}{\partial\theta_{c,j-1}^{i}}}{\partial\theta_{c,j-1}^{i}}\right]\cdot\theta_{c,0}^{i}}\\ &{\displaystyle\qquad\qquad\qquad\qquad\qquad=\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{c,B}^{i}}\cdot\left[\prod_{j=1}^{B}\mathbf{I}-\epsilon\frac{\partial^{2}\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{j-1})}{\partial(\theta_{c,j-1}^{i})^{2}}\right]\cdot\theta_{c,0}^{i},}\end{array}
$$

$$
\begin{array}{r l}&{\frac{\partial\mathcal{L}_{m}\left(\mu_{l}=0;\mathcal{D}_{B}\right)}{\partial\theta_{c,0}^{i}}\cdot\theta_{c,0}^{i}=\frac{\partial\mathcal{L}_{m}\left(\mu_{l}=0;\mathcal{D}_{B}\right)}{\partial\theta_{c,B}^{i}}\cdot\left[\displaystyle\prod_{j=1}^{B}\mathbf{I}-\epsilon\frac{\partial^{2}\mathcal{L}_{m}\left(\mu_{l}=0;\mathcal{D}_{j-1}\right)}{\partial(\theta_{c,j-1}^{i})^{2}}\right]\cdot\theta_{c,0}^{i}}\\ &{\phantom{\quad\quad}\approx\frac{\partial\mathcal{L}_{m}\left(\mu_{l}=0,\mathcal{D}_{B}\right)}{\partial\theta_{c,B}^{i}}\cdot\left[\displaystyle\prod_{j=1}^{B}\mathbf{I}\right]\cdot\theta_{c,0}^{i}}\\ &{\phantom{\quad\quad}=\frac{\partial\mathcal{L}_{m}\left(\mu_{l}=0,\mathcal{D}_{B}\right)}{\partial\theta_{c,B}^{i}}\cdot\theta_{c,0}^{i}.}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{l}}=|\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D})-\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{1})+\ldots+\mathcal{L}_{m}(;\mathcal{D}_{B-1})-\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{B})|}\\ &{\quad\quad=|\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D})-\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{B})|.}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{l,0}^{i}}=\big|\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})+\mu_{l}^{i}\odot\theta_{l,0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D})}{\partial\theta_{l,0}^{i}}}\\ &{\qquad\qquad\qquad\qquad-\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})-\mu_{l}^{i}\odot\theta_{l,0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{B})}{\partial\theta_{l,0}^{i}}\big|.}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{f}}(\mu_{c}=0)=|\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D})-\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{B})|,}\\ &{\tilde{\Phi}_{\theta_{f}}(\mu_{f}=0)=|\mathcal{L}_{m}(\mu_{f}=0;\mathcal{D})-\mathcal{L}_{m}(\mu_{f}=0;\mathcal{D}_{B})|,}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{f}^{i}}(\mu_{c}=0)=\big|\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D})+\mu_{f}^{i}\odot\theta_{f,0,\mu_{c}=0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D})}{\partial\theta_{f,0,\mu_{c}=0}^{i}}}\\ &{\qquad\qquad\qquad\qquad\qquad-\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{B})-\mu_{f}^{i}\odot\theta_{f,0,\mu_{c}=0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{B})}{\partial\theta_{f,0,\mu_{c}=0}^{i}}\big|,}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{f}^{i}}(\mu_{l}=0)=\big|\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})+\mu_{f}^{i}\odot\theta_{f,0,\mu_{l}=0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})}{\partial\theta_{f,0,\mu_{l}=0}^{i}}}\\ &{\qquad\qquad\qquad\qquad\qquad-\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})-\mu_{f}^{i}\odot\theta_{f,0,\mu_{l}=0}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{f,0,\mu_{l}=0}^{i}}\big|.}\end{array}
$$

$$
\begin{array}{r l}&{\tilde{\Phi}_{\theta_{f}^{i}}(\mu_{c}=0)=\big|\theta_{f}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D})}{\partial\theta_{f}^{i}}-\theta_{f}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{c}=0;\mathcal{D}_{B})}{\partial\theta_{f,B,\mu_{c}=0}^{i}}\big|,}\\ &{\tilde{\Phi}_{\theta_{l}^{i}}(\mu_{l}=0)=\big|\theta_{f}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D})}{\partial\theta_{f}^{i}}-\theta_{f}^{i}\cdot\frac{\partial\mathcal{L}_{m}(\mu_{l}=0;\mathcal{D}_{B})}{\partial\theta_{f,B,\mu_{l}=0}^{i}}\big|.}\end{array}
$$
