$$
\begin{array}{r l r}{\lefteqn{\sum_{\ell=0}^{\lfloor\log(t-B)\rfloor}b_{\ell}^{2}=\frac{1}{\varepsilon^{2}}\sum_{\ell=0}^{\lfloor\log(t-B)\rfloor}(\ell+1)^{2(1-\lambda)}\le\frac{1}{\varepsilon^{2}}\left(1+\int_{1}^{\log(t)+2}x^{2(1-\lambda)}\mathsf{d}x\right)}}\\ &{}&{\le\frac{1}{\varepsilon^{2}}\left(1+\left[\frac{1}{3-2\lambda}x^{3-2\lambda}\right]_{x=1}^{x=\log(t)+2}\right)=O\left(\frac{1+\log(t)^{3-2\lambda}}{\varepsilon^{2}}\right)~.}\end{array}
$$

$$
\begin{array}{r l}&{\quad\operatorname*{Pr}_{z}\lbrack A(x;z)\in S\rbrack=\operatorname*{Pr}_{z\in{\cal J}}\lbrack z\in\mathcal{N}_{S}\rbrack\leq e^{\varepsilon g(\tau-j)}\quad\operatorname*{Pr}_{z^{\prime}\sim{\cal Z}}\lbrack z^{\prime}\in q(\mathcal{N}_{S})\rbrack}\\ &{\qquad\qquad\qquad\qquad\leq e^{\varepsilon g(\tau-j)}\quad\operatorname*{Pr}_{z^{\prime}\sim{\cal Z}}\lbrack A(x^{\prime};z^{\prime})\in S\rbrack.}\end{array}
$$

$$
\begin{array}{r l r}{\lefteqn{\sum_{z\in{\cal Z}}[z\in\mathcal{N}]=\displaystyle\int_{z\in\mathcal{N}}\prod_{i=1}^{k}f_{\mathrm{Lap}}(b_{i})(z_{i})d z\leq\displaystyle\int_{z\in\mathcal{N}}\prod_{i=1}^{k}e^{|z_{i}^{\prime}-z_{i}|/b_{i}}f_{\mathrm{Lap}}(b_{i})(z_{i}^{\prime})d z}}\\ &{}&{=e^{s}\displaystyle\int_{z\in\mathcal{N}}\prod_{i=1}^{k}f_{\mathrm{Lap}}(b_{i})(z_{i}^{\prime})d z=e^{s}\displaystyle\int_{z\in q(\mathcal{N})}\prod_{i=1}^{k}f_{\mathrm{Lap}}(b_{i})(z_{i})d z}\\ &{}&{=e^{s}\displaystyle\operatorname*{Pr}_{z\in\mathcal{Z}}[z\in q(\mathcal{N})]}\end{array}
$$

$$
\begin{array}{r l}&{\sum_{i=1}^{t}x_{i}=\sum_{i=1}^{t}x_{i}^{\prime}\mathrm{~for~all~}t<j\mathrm{~and~}}\\ &{\sum_{i=1}^{t}x_{i}^{\prime}=\sum_{i=1}^{t}x_{i}+y\mathrm{~for~all~}t\geq j,\mathrm{~where~}y=x_{j}^{\prime}-x_{j}\in[-1,1].}\end{array}
$$

$$
\begin{array}{r l}&{\underset{z\in Z}{\operatorname*{Pr}}[z\in\mathcal{N}]\leq\exp\left(\displaystyle\sum_{\ell=0}^{\log T}\sum_{I\in D_{[j,\tau^{\prime}]}\cap\mathcal{Z}^{\ell}}\frac{|y|\varepsilon}{(1+\ell)^{1-\lambda}}\right)\underset{z\in Z}{\operatorname*{Pr}}[z\in q(\mathcal{N})]}\\ &{\qquad\qquad\leq\exp\left(2\varepsilon\displaystyle\sum_{\ell=0}^{\ell^{*}}(1+\ell)^{\lambda-1}\right)\underset{z\in Z}{\operatorname*{Pr}}[z\in q(\mathcal{N})]~.}\end{array}
$$

$$
\begin{array}{r l}&{g(d)=2\overbrace{\sum_{\ell=0}^{\lfloor\log(d-B+1)\rfloor}(1+\ell)^{\lambda-1}}^{\lfloor\log(d-B+1)\rfloor}}\\ &{\quad\le2\left(1+\overbrace{\sum_{\ell}\sum_{\ell=1}^{\infty}x^{\lambda-1}\,\mathrm{d}x}^{\left(d-B+1\right)+1}\right)}\\ &{\quad=2\left(1+\left[\frac{1}{\lambda}x^{\lambda}\right]_{x=1}^{x=\log(d-B+1)+1}\right)}\\ &{\quad=O(1+\log^{\lambda}(d-B+1))\ .}\end{array}
$$

$$
\begin{array}{r l}&{\operatorname*{lim}_{\lambda\to0}f(\lambda)=\underset{\lambda\to0}{\operatorname*{lim}}\frac{\frac{\mathsf{d}}{\mathsf{d}\lambda}\left(\left(\log\!\left(d-B+1\right)+1\right)^{\lambda}-1\right)}{\frac{\mathsf{d}}{\mathsf{d}\lambda}\lambda}}\\ &{\qquad\qquad=\underset{\lambda\to0}{\operatorname*{lim}}\log(\log\!\left(d-B+1\right))\log^{\lambda}\!\left(d-B+1\right)}\\ &{\qquad\qquad=\log\!\left(\log\!\left(d-B+1\right)\right)\!.}\end{array}
$$

$$
\begin{array}{r l}{\lefteqn{\sum_{\ell=0}^{\log t}b_{\ell}^{2}=\sum_{\ell=0}^{\log t}\frac{(\ell+1)^{2(1-\lambda)}}{\varepsilon^{2}}}}\\ &{\le\frac{1}{\varepsilon^{2}}\left(1+\intop_{1}^{\log(t)+2}x^{2(1-\lambda)}\mathsf{d}x\right)}\\ &{\le\frac{1}{\varepsilon^{2}}\left(1+\left[\frac{1}{3-2\lambda}x^{3-2\lambda}\right]_{x=1}^{x=\log(t)+2}\right)\ .}\end{array}
$$

$$
\begin{array}{r l}&{2/3\leq\operatorname*{Pr}[A(x_{1}^{(i)},\ldots,x_{t}^{(i)})_{t=1}^{2C i}\in S_{i}]}\\ &{\qquad\leq e^{\sum_{j=0}^{2C-1}g(j)\varepsilon}\operatorname*{Pr}[A(x_{1}^{(0)},\ldots,x_{t}^{(0)})_{t=1}^{2C i}\in S_{i}]}\\ &{\qquad=e^{\sum_{j=0}^{2C-1}g(j)\varepsilon}\operatorname*{Pr}[A(x_{1}^{(0)},\ldots,x_{t}^{(0)})_{t=1}^{T^{\prime}}\in S_{i}]}\end{array}
$$

$$
\begin{array}{r l}&{1\ge\displaystyle\sum_{i=1}^{T^{\prime}/(2C)}\operatorname*{Pr}[A(x_{1}^{(0)},\ldots,x_{t}^{(0)})_{t=1}^{T^{\prime}}\in S_{i}]}\\ &{~~\ge\displaystyle\sum_{i=1}^{T^{\prime}/(2C)}(2/3)e^{-\sum_{j=0}^{2C-1}g(j)\varepsilon}}\\ &{~~=(T^{\prime}/(2C))(2/3)e^{-\sum_{j=0}^{2C-1}g(j)\varepsilon}.}\end{array}
