$$
\begin{array}{r l}&{\pi_{\theta^{\infty}}(a_{1}|x)=\frac{e^{\theta_{\infty}^{\infty}\phi(x)}}{e^{\theta_{\infty}^{\infty}\phi(x)}+e^{\theta_{\infty}^{\infty}\phi(x)}}}\\ &{\qquad\qquad=\frac{e^{(\theta_{1}+\delta_{x})\phi(x)}}{e^{(\theta_{1}+\delta_{x})\phi(x)}+e^{(\theta_{2}-\delta_{x})\phi(x)}}}\\ &{\qquad=\frac{e^{\theta_{1}\phi(x)}}{e^{\theta_{1}\phi(x)}+e^{(\theta_{2}-2\delta_{x})\phi(x)}}}\\ &{\qquad\qquad\qquad\qquad\qquad e^{\theta_{1}\phi(x)}}\\ &{\qquad=\frac{e^{\theta_{1}\phi(x)}}{e^{\theta_{1}\phi(x)}+e^{\theta_{2}\phi(x)}-2\delta_{x}\phi(x)}}\\ &{\qquad\geq\frac{e^{\theta_{1}\phi(x)}}{e^{\theta_{1}\phi(x)}+e^{\theta_{2}\phi(x)}}\quad(\mathrm{since}\ -2\delta_{x}\phi(x)\leq0)}\\ &{\qquad=\pi_{\theta}(a_{1}|x)}\end{array}
$$

$$
\begin{array}{r l}&{\pi_{\theta^{\infty}}(a_{1}|y)=\frac{e^{\theta_{1}^{\infty}\alpha\phi(x)}}{e^{\theta_{1}^{\infty}\alpha\phi(x)}+e^{\theta_{2}^{\infty}\alpha\phi(x)}}}\\ &{\qquad\qquad\qquad\qquad e^{(\theta_{1}+\delta_{x})\alpha\phi(x)}}\\ &{\qquad=\frac{e^{(\theta_{1}+\delta_{x})\alpha\phi(x)}+e^{(\theta_{2}-\delta_{x})\alpha\phi(x)}}{e^{\theta_{1}\alpha\phi(x)}+e^{(\theta_{2}-2\delta_{x})\alpha\phi(x)}}}\\ &{\qquad=\frac{e^{\theta_{1}\alpha\phi(x)}}{e^{\theta_{1}\alpha\phi(x)}+e^{(\theta_{2}-2\delta_{x})\alpha\phi(x)}}}\\ &{\qquad=\frac{e^{\theta_{1}\alpha\phi(x)}}{e^{\theta_{1}\alpha\phi(x)}+e^{\theta_{2}\alpha\phi(x)}-2\delta_{x}\alpha\phi(x)}}\\ &{\qquad\geq\frac{e^{\theta_{1}\alpha\phi(x)}}{e^{\theta_{1}\alpha\phi(x)}+e^{\theta_{2}\alpha\phi(x)}}\quad(\mathrm{since~}-2\delta_{x}\alpha\phi(x)\leq0)}\\ &{\qquad=\pi_{\theta}(a_{1}|y)}\end{array}
$$

$$
\begin{array}{r l}&{\pi\rho\mathrm{e}^{-\mathrm{i}\phi}(x)\pi}\\ &{=\frac{e^{\theta_{1}^{\mathrm{eq}}\cdot\phi}(x)+e^{\theta_{2}^{\mathrm{eq}}\cdot\phi(x)}}{e^{\theta_{1}^{\mathrm{eq}}\cdot\phi(x)}+e^{\theta_{2}^{\mathrm{eq}}\cdot\phi(x)}}}\\ &{\qquad\qquad=\frac{e^{\theta_{1}+\delta_{y})\phi(x)}}{e^{\theta_{1}+\delta_{y})\phi(x)}+e^{\theta_{2}-\delta_{y})\phi(x)}}}\\ &{\quad=\frac{e^{\theta_{1}\phi(x)}}{e^{\theta_{1}\phi(x)}+e^{\theta_{2}-\delta_{y})\phi(x)}}}\\ &{\quad=\frac{e^{\theta_{1}\phi(x)}}{e^{\theta_{1}\phi(x)}+e^{\theta_{2}\phi(x)}-2\delta_{y}\phi(x)}}\\ &{\quad\geq\frac{e^{\theta_{1}\phi(x)}}{e^{\theta_{1}\phi(x)}+e^{\theta_{2}\phi(x)}}\quad\mathrm{(since~-~}2\delta_{y}\phi(x)\leq0)}\\ &{\quad=\pi\theta(a_{1})x}\end{array}
$$

$$
\begin{array}{r l}&{\pi_{\theta^{\infty}}(a_{1}|y)=\frac{e^{\theta_{1}^{\infty}\alpha\phi(x)}}{e^{\theta_{1}^{\infty}\alpha\phi(x)}+e^{\theta_{2}^{\infty}\alpha\phi(x)}}}\\ &{=\frac{e^{(\theta_{1}+\delta_{y})\alpha\phi(x)}}{e^{(\theta_{1}+\delta_{y})\alpha\phi(x)}+e^{(\theta_{2}-\delta_{y})\alpha\phi(x)}}}\\ &{=\frac{e^{\theta_{1}\alpha\phi(x)}}{e^{\theta_{1}\alpha\phi(x)}+e^{(\theta_{2}-2\delta_{y})\alpha\phi(x)}}}\\ &{=\frac{e^{\theta_{1}\alpha\phi(x)}}{e^{\theta_{1}\alpha\phi(x)}+e^{\theta_{2}\alpha\phi(x)}-2\delta_{y}\alpha\phi(x)}}\\ &{\geq\frac{e^{\theta_{1}\alpha\phi(x)}}{e^{\theta_{1}\alpha\phi(x)}+e^{\theta_{2}\alpha\phi(x)}}\quad\mathrm{(since~-~}2\delta_{y}\alpha\phi(x)\leq0\mathrm{)}}\\ &{=\pi(a_{1},\alpha x,\theta)}\\ &{=\pi_{\theta}(a_{1}|y)}\end{array}
$$

$$
