$$
\begin{array}{r l}{V(x)}&{=\;\mathbb{E}_{\tau\sim\tau_{P}}[\langle t\rangle\langle x(0)=x|}\\ &{=\;\mathbb{E}_{\tau\sim\tau_{P}}\Bigg[-\sum_{\tau=0}^{\tau}\mathrm{t}\ln\tau^{\alpha}(\alpha(t)|x(t))\,p^{\beta}\left(x(t+1)|x(t),\alpha(t)\right)|\;x(0)=x\Bigg]}\\ &{=\;-\sum_{\tau=0}^{\tau}\frac{\tau}{x}\pi(a|x)p(x^{\prime}|x,\alpha)\sum_{\tau=0}^{\tau}\mathrm{t}\ln\left(\alpha^{\alpha}(a|t)|x(t)\right)p^{\beta}\left(x(t+1)|x(t),\alpha(t)\right)\Bigg\}_{x(0)=x}}\\ &{=\;-\sum_{\tau=0}^{\tau}\pi(a|x)p(x^{\prime}|x,\alpha)\Bigg[\ln\tau^{\alpha}(a|x|p^{\beta}(x^{\prime}|x,\alpha))\Bigg.}\\ &{\qquad\Bigg.+\frac{\tau}{\gamma}\sum_{\tau=0}^{\tau}t(a|x)p(x^{\prime}|x,\alpha(t)|x(t),p^{\beta}(x^{\prime}|x,\alpha(t)))\Bigg.}\\ &{\qquad+\left.\gamma\sum_{\sigma}^{\sigma}\mathrm{t}\ln\left(\alpha^{\sigma}(a|t)|x(t)\right)p^{\beta}\left(x(t+1)|x(t),\alpha(t)\right)\right)\Bigg|_{\alpha(0)=x}\Bigg]}\\ &{=\;-\sum_{\tau}\pi(a|x)\sin\pi(a|x)-\sum_{\sigma}\pi(a|x)\sum_{\tau}p(x^{\prime}|x,\alpha)\beta\ln p(x^{\prime}|x,\alpha)+}\\ &{\qquad+\gamma\mathbb{E}[\mu(\tau)|x(0)=x]}\\ &{=\;\alpha\hbar(A|x)+\beta\sum_{\tau}\pi(a|x)\hbar(\delta|x,\alpha)+\gamma\sum_{\tau}\pi(a|x)\sum_{p}p(x^{\prime}|x,\alpha)V(x^{\prime})\;,}\end{array}
$$

$$
{\begin{array}{r c l}{\lambda(x,x)}&{=}&{{\displaystyle{\frac{\partial V(x)}{\partial\pi(a|x)}}\Biggr\vert_{x=x^{\prime}}}}\\ &{=}&{{\displaystyle{\frac{\partial}{\partial\pi(a|x)}}\Biggl[-\sum_{a^{\prime}}\pi(a^{\prime}|x)\Biggl(\alpha\ln\pi(a^{\prime}|x)+\sum_{x^{\prime}}p(x^{\prime}|x,a^{\prime})\bigl(\beta\ln p(x^{\prime}|x,a^{\prime})-}\\ &&{-\gamma V(x^{\prime})\bigr)\Biggr)\Biggr]_{\parallel_{\mathbf{r}=\pi^{*}}}}\\ &{=}&{-\gamma\ln\pi^{*}(a|x)+\sum_{x^{\prime}}p(x^{\prime}|x,a)\left(-\beta\ln p(x^{\prime}|x,a)+\gamma V_{\pi^{*}}(x^{\prime})\right)-}\\ &&{-\alpha+\gamma\sum_{a^{\prime}}\pi^{*}(a^{\prime}|x)\sum_{x^{\prime}}p(x^{\prime}|x,a^{\prime})\lambda(x^{\prime},x)}\\ &{=}&{-\alpha\ln\pi^{*}(a|x)-\beta\sum_{a^{\prime}}p(x^{\prime}|x,a)\ln p(x^{\prime}|x,a)+\gamma\sum_{a^{\prime}}p(x^{\prime}|x,a)V_{\pi^{*}}(x^{\prime})+h(14)}\end{array}}
$$

$$
\begin{array}{r l}{V^{c}(x)}&{=}&{\displaystyle\sum_{a}\pi^{c}(a|x)\sum_{s^{\prime}}p(x^{\prime}|x,a)\left[-\arcsin\pi^{c}(a|x)-\beta\ln p(x^{\prime}|x,a)\right]+\gamma\Re[V^{c}(x^{\prime})]\;}\\ &{=}&{\displaystyle\sum_{a}\pi^{c}(a|x)\sum_{s^{\prime}}p(x^{\prime}|x,a)\Biggl[\Biggl.\Biggl.}\\ &{\quad-\Biggl.\Biggl.\.\alpha\ln\left(e^{\frac{1}{a}\left(\beta\hbar(\delta|x,a)+\gamma\sum_{s^{\prime}}p(x^{\prime}|x,a)\right)\gamma^{c}(x^{\prime})}\right))\Biggr]+\gamma\mathbb{E}_{\pi^{c}}\left[V^{c}(x^{\prime})\right]}\\ &{=}&{\displaystyle\alpha\ln Z(x)-\sum_{a}\pi^{c}(a|x)\left(\beta\hbar(\delta|x,a)+\gamma\sum_{s^{\prime}}p(x^{\prime}|x,a)V^{c}(x^{\prime})\right)+}\\ &{\quad+\sum_{a}\pi^{c}(a|x)\beta\hbar(\delta|x,a)+\gamma\mathbb{E}_{\pi^{c}}\bigl[V^{c}(x^{\prime})\bigr]}\\ &{=}&{\displaystyle\alpha\ln Z(x)=\operatorname*{chn}_{a\in A(x)}\sum_{a\in A(x)}e^{\frac{1}{a}\left(\beta\hbar(\delta|x,a)+\gamma\sum_{s^{\prime}}p(x^{\prime}|x,a)V_{a^{\prime}}(x^{\prime})\right)}\,,}\end{array}
$$

