$$
F S W_{r}(P,Q)=\left(\int_{S}W_{r}(c_{s}\#P,c_{s}\#Q)^{r}d s\right)^{1/r}
$$

$$
\begin{array}{r l r}{\lefteqn{F S W_{r}(P,P)=\left(\displaystyle\int_{S}W_{r}(c_{s}\#P,c_{s}\#P)^{r}d s\right)^{1/r}}}\\ &{}&{=\left(\displaystyle\int_{S}0^{r}d s\right)^{1/r}}\\ &{}&{=0}\end{array}
$$

$$
\begin{array}{r l r}{\lefteqn{F S W_{r}(P,Q)=\left(\int_{S}W_{r}(c_{s}\#P,c_{s}\#Q)^{r}d s\right)^{1/r}}}\\ &{}&{\qquad=\left(\int_{S}W_{r}(c_{s}\#Q,c_{s}\#P)^{r}d s\right)^{1/r}}\\ &{}&{\qquad=F S W_{r}(Q,P)}\end{array}
$$

$$
\begin{array}{r l}&{F S W_{r}(P,U)=\left(\displaystyle\int_{S}W_{r}(c_{s}\#P,c_{s}\#U)^{r}d s\right)^{1/r}}\\ &{\qquad\qquad\leq\left(\displaystyle\int_{S}\left[W_{r}(c_{s}\#P,c_{s}\#Q)+W_{r}(c_{s}\#Q,c_{s}\#U)\right]^{r}d s\right)^{1/r}}\\ &{\qquad\qquad\leq\left(\displaystyle\int_{S}W_{r}(c_{s}\#P,c_{s}\#Q)^{r}d s\right)^{1/r}+\left(\displaystyle\int_{S}W_{r}(c_{s}\#Q,c_{s}\#U)^{r}d s\right)^{1/r}}\\ &{\qquad=F S W_{r}(P,Q)+F S W_{r}(Q,U)}\end{array}
$$

$$
\begin{array}{r l}&{F S W_{r}(P,\lambda Q+(1-\lambda)U)=\left(\displaystyle\int_{\mathcal{S}}W_{r}(c_{s}\#P,c_{s}\#\{\lambda Q+(1-\lambda)U\})^{r}d s\right)^{1/r}}\\ &{\qquad\qquad\qquad\qquad=\left(\displaystyle\int_{\mathcal{S}}W_{r}(c_{s}\#P,\lambda c_{s}\#Q+(1-\lambda)c_{s}\#U)^{r}d s\right)^{1/r}}\\ &{\qquad\qquad\leq\left(\displaystyle\int_{\mathcal{S}}\lambda W_{r}(c_{s}\#P,c_{s}\#Q)^{r}+(1-\lambda)W_{r}(c_{s}\#P,c_{s}\#U)^{r}d s\right)^{1/r}}\\ &{\qquad\qquad\leq\left(\lambda\displaystyle\int_{\mathcal{S}}W_{r}(c_{s}\#P,\lambda c_{s}\#Q)^{r}d s\right)^{1/r}+}\\ &{\qquad\qquad\qquad\Big((1-\lambda)\displaystyle\int_{\mathcal{S}}W_{r}(c_{s}\#P,c_{s}\#U)^{r}d s\Big)^{1/r}}\\ &{\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\qquad\quad1/r}\\ &{\qquad\qquad=\lambda^{1/r}F S W_{r}(P,Q)+(1-\lambda)^{1/r}F S W_{r}(P,U)}\end{array}
$$
