$$
\begin{array}{r l}&{^{\circ}(t)-P(t-1)=D({\pmb x}(t),{\pmb y}(t))-D({\pmb x}(t-1),{\pmb y}(t-1))}\\ &{\qquad\qquad\qquad=D({\pmb x}(t),{\pmb y}(t))-D({\pmb x}(t-1),{\pmb y}(t))+D({\pmb x}(t-1),{\pmb y}(t))-D({\pmb x}(t-1),{\pmb y}(t-1)}\\ &{\qquad\qquad\qquad\leq-\mathrm{OT}({\pmb x}(t-1),{\pmb x}(t))+(1+\epsilon))\mathrm{OT}({\pmb y}(t-1),{\pmb y}(t)),}\end{array}
$$

$$
\begin{array}{r}{\ensuremath{\operatorname{Or}}(x(t-1),x(t))\leq P(t-1)-P(t)+(1+\epsilon)\ensuremath{\operatorname{Or}}(y(t-1),y(t)).}\end{array}
$$

$$
\begin{array}{r l}&{\displaystyle\sum_{t\geq1}\mathrm{Or}(x(t-1),x(t))\leq\displaystyle\sum_{t\geq1}P(t-1)-P(t)+(1+\epsilon)\mathrm{OT}(y(t-1),y(t))}\\ &{\quad\qquad\qquad\qquad\qquad\leq P(0)+(1+\epsilon)\displaystyle\sum_{t\geq1}\mathrm{OT}(y(t-1),y(t)),}\end{array}
$$

$$
\begin{array}{r}{\displaystyle\int D(\pmb{x}(t),\pmb{y}(t))+\operatorname{OT}(\pmb{x}(t-1),\pmb{x}(t))=D(\pmb{x}^{\prime},\pmb{y}(t))+\operatorname{OT}(\pmb{x}(t-1),\pmb{x}^{\prime}),}\\ {\operatorname{OT}(\pmb{x}(t-1),\pmb{x}^{\prime})=\operatorname{OT}(\pmb{x}(t-1),\pmb{x}(t))+\operatorname{OT}(\pmb{x}(t),\pmb{x}^{\prime}).}\end{array}
$$
